CREATE OR REPLACE PROCEDURE inserir_relatorio_perfil (cd_perfil_destino_p bigint, nr_seq_relatorio_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_param_w		bigint;
nr_sequencia_w		bigint;


BEGIN

select	nextval('relatorio_perfil_seq')
into STRICT	nr_sequencia_w
;

insert	into RELATORIO_PERFIL(NR_SEQUENCIA,
		CD_PERFIL,
		NR_SEQ_RELATORIO,
		DT_ATUALIZACAO,
		NM_USUARIO,
		QT_COPIA,
		IE_LOGO,
		IE_CONFIGURAR,
		IE_GRAVAR_LOG,
		IE_APRESENTA)
	values (nr_sequencia_w,
		cd_perfil_destino_p,
		nr_seq_relatorio_p,
		clock_timestamp(),
		nm_usuario_p,
		1,
		'S',
		'N',
		'N',
		'S');

insert into relatorio_perfil_param(
			nr_seq_perfil_relat,
			nr_seq_parametro,
			dt_atualizacao,
			nm_usuario)
		(SELECT	nr_sequencia_w,
			nr_sequencia,
			clock_timestamp(),
			nm_usuario_p
		from	relatorio_parametro
		where	nr_seq_relatorio = nr_seq_relatorio_p);

commit;

end;
$body$
