CREATE OR REPLACE PROCEDURE inserir_resposta_pesq_satisf ( nr_seq_pergunta_p pesquisa_satisf_pergunta.nr_sequencia%type, nr_seq_opcao_p pesquisa_satisf_resposta.nr_seq_opcao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_setor_atendimento_p setor_atendimento.cd_setor_atendimento%type, nm_usuario_p usuario.nm_usuario%type, ds_justificativa_p pesquisa_satisf_resposta.ds_justificativa%type, ie_resposta_anonima_p pesquisa_satisf_pergunta.ie_resposta_anonima%type default 'N') AS $body$
DECLARE

	
	ds_valor_w		varchar(3);
	nr_seq_gerencia_w	bigint;
	nr_seq_grupo_w		bigint;
	usuario_w		usuario.nm_usuario%type;
	nm_usuario_hash_w 	varchar(50);
	

BEGIN
	
if (nr_seq_pergunta_p IS NOT NULL AND nr_seq_pergunta_p::text <> '' AND nr_seq_opcao_p IS NOT NULL AND nr_seq_opcao_p::text <> '') then
	begin
	select a.ds_valor into STRICT ds_valor_w from pesquisa_satisf_opcao a where a.nr_sequencia = nr_seq_opcao_p;
	
	select	max(y.nr_seq_gerencia), max(y.nr_sequencia)
		into STRICT	nr_seq_gerencia_w, nr_seq_grupo_w
		from	gerencia_wheb g,
			gerencia_wheb_grupo y,
			gerencia_wheb_grupo_usu x
		where	y.nr_sequencia = x.nr_seq_grupo
		and	y.nr_seq_gerencia = g.nr_sequencia
		and	upper(x.nm_usuario_grupo) = upper(nm_usuario_p)
		and	clock_timestamp() between coalesce(x.dt_inicio,clock_timestamp()) and coalesce(x.dt_fim,clock_timestamp())
		and	coalesce(y.ie_situacao,'A') = 'A'
		and	coalesce(g.ie_situacao,'A') = 'A';
	
	if (ie_resposta_anonima_p = 'S') then
		usuario_w := null;
		nm_usuario_hash_w := substr(get_md5_hash_value(nm_usuario_p || nr_seq_pergunta_p),0,50);
	else
		usuario_w := nm_usuario_p;
		nm_usuario_hash_w := null;
	end if;
	
	insert	into pesquisa_satisf_resposta(
		nr_sequencia,
		nr_seq_pergunta,
		nr_seq_opcao,
		cd_estabelecimento,
		cd_setor_atendimento,
		ds_opcao,
		nr_seq_gerencia,
		nr_seq_grupo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_justificativa,
		nm_usuario_hash)
	values (nextval('pesquisa_satisf_resposta_seq'),
		nr_seq_pergunta_p,
		nr_seq_opcao_p,
		cd_estabelecimento_p,
		cd_setor_atendimento_p,
		ds_valor_w,
		nr_seq_gerencia_w,
		nr_seq_grupo_w,
		clock_timestamp(),
		usuario_w,
		clock_timestamp(),
		usuario_w,
		ds_justificativa_p,
		nm_usuario_hash_w
	);
	end;
end if;

commit;

end;
$body$
