CREATE OR REPLACE PROCEDURE inserir_rxt_agenda_integracao ( cd_pessoa_fisica_p text, cd_agendamento_externo_p text, dt_agenda_p timestamp, nr_minuto_duracao_p integer, cd_equip_externo_p text, ds_integracao_p text, tipo_mensagem_p text, codigo_mensagem_p text ) AS $body$
DECLARE


nr_seq_tratamento_w varchar(10);
nr_seq_equipamento_w bigint;
nr_seq_tumor_w bigint;
nr_seq_rxt_tipo_w bigint;
ie_tipo_equip_w varchar(20);


BEGIN

  if ('SIU' = tipo_mensagem_p and 'S12' = codigo_mensagem_p) then

	select max(nr_sequencia),
  	coalesce(max(ie_tipo),'A')
	into STRICT nr_seq_equipamento_w,
  	ie_tipo_equip_w
	from rxt_equipamento
	where cd_equip_externo = cd_equip_externo_p;

	select max(a.nr_sequencia)
	into STRICT nr_seq_rxt_tipo_w
	from rxt_tipo a
	where exists (SELECT 1 from rxt_equip_tipo b
	where b.nr_seq_equipamento = nr_seq_equipamento_w
	and nr_seq_tipo = a.nr_sequencia)
	and a.ie_situacao = 'A';

	select max(nr_sequencia)
	into STRICT nr_seq_tumor_w
	from rxt_tumor
	where cd_pessoa_fisica = cd_pessoa_fisica_p
	and nr_seq_tipo = nr_seq_rxt_tipo_w;

	select max(nr_sequencia)
	into STRICT nr_seq_tratamento_w
	from rxt_tratamento
	where nr_seq_tumor = nr_seq_tumor_w;

 	 if (nr_seq_tratamento_w IS NOT NULL AND nr_seq_tratamento_w::text <> '' AND nr_seq_equipamento_w IS NOT NULL AND nr_seq_equipamento_w::text <> '') then
  
		insert into rxt_agenda(
		nr_sequencia,
		dt_atualizacao_nrec,     
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,  
		nr_seq_tratamento,  
		nr_seq_equipamento,
		dt_agenda,      
		nr_minuto_duracao,
		ie_status_agenda,
		dt_agendamento,
  		ie_tipo_agenda,
  		cd_agendamento_externo
		) values (
		nextval('rxt_agenda_seq'),
		clock_timestamp(),
		ds_integracao_p,
		clock_timestamp(),
		ds_integracao_p,
		nr_seq_tratamento_w,
		nr_seq_equipamento_w,
		dt_agenda_p,
		nr_minuto_duracao_p,
		'M',
		clock_timestamp(),
  		CASE WHEN ie_tipo_equip_w='A' THEN  'T'  ELSE 'S' END ,
 		cd_agendamento_externo_p
		);
  	end if;

  else
  	update rxt_agenda
  	set dt_agenda = dt_agenda_p
  	where cd_agendamento_externo = cd_agendamento_externo_p;
  end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
