SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_seq_forn_interna ( cd_cgc_p INOUT text, cd_tipo_pessoa_p bigint) AS $body$
DECLARE



cd_cnpj_intern_w		varchar(255);
ie_incrementar_pj_w		varchar(1);

BEGIN

select	coalesce(ie_incrementar_pj,'N')
into STRICT		ie_incrementar_pj_w
from	tipo_pessoa_juridica
where	cd_tipo_pessoa  = cd_tipo_pessoa_p;


if (ie_incrementar_pj_w = 'S') then
	select 	adiciona_zeros_esquerda(coalesce(max(nr_ultimo_cnpj),0)+1,14)
	into STRICT	cd_cnpj_intern_w
	from 	parametros_pj
	where	ie_incrementar_pj = 'S';


	cd_cgc_p		:=  cd_cnpj_intern_w;

	update	parametros_pj
	set	nr_ultimo_cnpj =  cd_cnpj_intern_w;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
