CREATE OR REPLACE PROCEDURE inserir_servico_pad ( nr_sequencia_p text, ds_lista_p text, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w 		bigint;
lista_w	dbms_sql.varchar2_table;
nr_seq_servico_w	varchar(10);


BEGIN

lista_w	:= obter_lista_string(ds_lista_p,',');

if (lista_w.count	>0) then

	for i in lista_w.first..lista_w.last loop
			
		begin
		
		nr_seq_servico_w	:= lista_w(i);
		
		select nextval('hc_pad_servico_seq')
		into STRICT nr_sequencia_w
		;
		
		insert into hc_pad_servico(	nr_sequencia,
										dt_atualizacao,
										dt_atualizacao_nrec,
										nm_usuario,
										nm_usuario_nrec,
										nr_seq_controle,
										nr_seq_servico) values (	nr_sequencia_w,
																	clock_timestamp(),
																	clock_timestamp(),
																	nm_usuario_p,
																	nm_usuario_p,
																	nr_sequencia_p,
																	nr_seq_servico_w);
																	
		commit;
			

		end;
	end loop;
end if;
							 				
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
