SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_serv_prescr_aval ( nr_prescricao_p bigint, nr_seq_serv_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		integer;
nr_prescricao_w		bigint;
nr_atendimento_w	bigint;
dt_servico_w		timestamp;
qt_prescricoes_w	integer;

c01 CURSOR FOR
SELECT	a.nr_prescricao
from 	PRESCR_MEDICA a
Where	1 = 1
and	a.nr_atendimento = nr_atendimento_w
and  	(coalesce(a.dt_liberacao,a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao,a.dt_liberacao_medico))::text <> '')
and  	(( dt_servico_w between a.dt_inicio_prescr and a.dt_validade_prescr)
  	or (coalesce(a.dt_validade_prescr::text, '') = ''))
and	((exists (select 1 from prescr_dieta b where (a.nr_prescricao = b.nr_prescricao))) or (exists (select 1 from rep_jejum c where (a.nr_prescricao = c.nr_prescricao)))    or (exists (select 1 from prescr_material d where (a.nr_prescricao = d.nr_prescricao and d.ie_agrupador in (8,12)))) or (exists (select 1 from nut_pac e where (a.nr_prescricao = e.nr_prescricao))) or (exists (select 1 from prescr_leite_deriv e where e.nr_prescricao = a.nr_prescricao)));



BEGIN

select	dt_Servico,
	nr_Atendimento
into STRICT	dt_servico_w,
	nr_atendimento_w
from	nut_Atend_Serv_dia a
where	a.nr_sequencia = nr_seq_serv_dia_p;

select	count(*)
into STRICT	qt_prescricoes_w
from 	PRESCR_MEDICA a
Where	1 = 1
and	a.nr_atendimento = nr_atendimento_w
and  	(coalesce(a.dt_liberacao,a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao,a.dt_liberacao_medico))::text <> '')
and  	(( dt_servico_w between a.dt_inicio_prescr and a.dt_validade_prescr)
  	or (coalesce(a.dt_validade_prescr::text, '') = ''))
and	((exists (SELECT 1 from prescr_dieta b where (a.nr_prescricao = b.nr_prescricao))) or (exists (select 1 from rep_jejum c where (a.nr_prescricao = c.nr_prescricao)))    or (exists (select 1 from prescr_material d where (a.nr_prescricao = d.nr_prescricao and d.ie_agrupador in (8,12)))) or (exists (select 1 from nut_pac e	where (a.nr_prescricao = e.nr_prescricao))) or (exists (select 1 from prescr_leite_deriv e where e.nr_prescricao = a.nr_prescricao)));

if (coalesce(nr_prescricao_p::text, '') = '' and qt_prescricoes_w > 0) then
	open c01;
	loop
	fetch c01 into nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	count(*)
		into STRICT	qt_registros_w
		from	nut_atend_serv_prescr_aval a
		where	a.nr_prescricao = nr_prescricao_w
		and	nr_seq_serv_dia = nr_seq_serv_dia_p;

		if (qt_registros_w = 0) then
			insert into nut_atend_serv_prescr_aval(	nr_sequencia,
								nr_prescricao,
								nr_seq_serv_dia,
								dt_atualizacao,
								dt_atualizacao_nrec,
								nm_usuario,
								nm_usuario_nrec)
							values (	nextval('nut_atend_serv_prescr_aval_seq'),
								nr_prescricao_w,
								nr_seq_serv_dia_p,
								clock_timestamp(),
								clock_timestamp(),
								nm_usuario_p,
								nm_usuario_p);
		end if;
		end;
	end loop;
	close c01;

else
	select	count(*)
		into STRICT	qt_registros_w
		from	nut_atend_serv_prescr_aval a
		where	a.nr_prescricao = nr_prescricao_p
		and	nr_seq_serv_dia = nr_seq_serv_dia_p;

		if (qt_registros_w = 0) then
			insert into nut_atend_serv_prescr_aval(	nr_sequencia,
								nr_prescricao,
								nr_seq_serv_dia,
								dt_atualizacao,
								dt_atualizacao_nrec,
								nm_usuario,
								nm_usuario_nrec)
							values (	nextval('nut_atend_serv_prescr_aval_seq'),
								nr_prescricao_p,
								nr_seq_serv_dia_p,
								clock_timestamp(),
								clock_timestamp(),
								nm_usuario_p,
								nm_usuario_p);
		end if;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
