CREATE OR REPLACE PROCEDURE inserir_setores_grupo ( nm_usuario_p text, nr_seq_classif_agrup_p bigint) AS $body$
DECLARE


cd_setor_atendimento_w	integer;

C01 CURSOR FOR
SELECT	a.cd_setor_atendimento
from	grupo_setor_item a
where	a.nr_seq_grupo = nr_seq_classif_agrup_p
and	not exists (	select	1
			 from	setor_atendimento c,
				agrup_classif_setor d,
				usuario_setor e
			 where	d.nr_sequencia = c.nr_seq_agrup_classif
			 and	d.nr_sequencia = nr_seq_classif_agrup_p
			 and	e.cd_setor_atendimento = c.cd_setor_atendimento
			 and	e.cd_setor_atendimento = a.cd_setor_atendimento
			 and	e.nm_usuario = nm_usuario_p)
and	not exists (	select	1
			from	usuario_setor x
			where	x.cd_setor_atendimento = a.cd_setor_atendimento
			and	x.nm_usuario_param = nm_usuario_p);


BEGIN

open c01;
loop
fetch c01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	insert into usuario_setor(
			nm_usuario_param,
			cd_setor_atendimento,
			dt_atualizacao,
			nm_usuario)
		values (	nm_usuario_p,
			cd_setor_atendimento_w,
			clock_timestamp(),
			nm_usuario_p);
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
