CREATE OR REPLACE PROCEDURE inserir_teste_forca (nr_sequencia_p bigint, nr_seq_avaliacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_musc_w			bigint;
qt_valor_w				bigint;
nr_seq_presc_musc_w		bigint;

c01 CURSOR FOR
		SELECT 	nr_seq_musc,
				qt_valor
		FROM 	hfp_protocolo_item
		WHERE	nr_seq_protocolo = nr_sequencia_p;

BEGIN

	open c01;
		loop
			fetch c01 into
				nr_seq_musc_w,
				qt_valor_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
				begin

					insert into hfp_prescr_musc(
								nr_sequencia,
								dt_atualizacao,
								nm_usuario,
								qt_valor,
								nr_seq_musc,
								nr_seq_aval)
							values (
								nextval('hfp_prescr_musc_seq'),
								clock_timestamp(),
								nm_usuario_p,
								qt_valor_w,
								nr_seq_musc_w,
								nr_seq_avaliacao_p);

			end;
		end loop;
	close c01;

commit;

end;
