CREATE OR REPLACE PROCEDURE inserir_tipo_gp_grupo_apap (nr_seq_grupo_apap_p bigint, ds_lista_tipo_gp_p text, nm_usuario_p text) AS $body$
DECLARE


lista_tipo_gp_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w		smallint;
nr_seq_tipo_gp_w		bigint;
nr_seq_grupo_gp_w	bigint;
nr_seq_inf_w		bigint;
ie_contador_w		bigint := 0;


BEGIN
lista_tipo_gp_w	:= ds_lista_tipo_gp_p;

while(lista_tipo_gp_w IS NOT NULL AND lista_tipo_gp_w::text <> '') or (ie_contador_w > 200) loop
	begin

	tam_lista_w	:= length(lista_tipo_gp_w);
	ie_pos_virgula_w	:= position(',' in lista_tipo_gp_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_tipo_gp_w	:= substr(lista_tipo_gp_w,1,(ie_pos_virgula_w - 1));
		lista_tipo_gp_w	:= substr(lista_tipo_gp_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	select	nr_seq_grupo
	into STRICT	nr_seq_grupo_gp_w
	from	tipo_perda_ganho
	where	nr_sequencia = nr_seq_tipo_gp_w;

	select	nextval('pep_apap_inf_seq')
	into STRICT	nr_seq_inf_w
	;

	insert into pep_apap_inf(
		nr_sequencia,
		nr_seq_apap,
		nr_seq_grupo_apap,
		nr_seq_apres,
		ie_manter_nulo,
		ie_grafico,
		nr_seq_grupo_pg,
		nr_seq_tipo_pg,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario)
	values (
		nr_seq_inf_w,
		null,
		nr_seq_grupo_apap_p,
		999,
		'S',
		'N',
		nr_seq_grupo_gp_w,
		nr_seq_tipo_gp_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p);

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
