SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_tit_rec_negociado ( nr_seq_negociacao_p bigint, ds_lista_titulo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w		bigint;
vl_saldo_titulo_w	double precision;
qt_registro_w		double precision;
vl_juros_w		double precision;
vl_multa_w		double precision;
dt_prev_pagto_w		timestamp;

c01 CURSOR FOR
SELECT	nr_titulo,
	vl_saldo_titulo
from	titulo_receber
where	' ' || ds_lista_titulo_p || ' ' like '% ' || nr_titulo || ' %'
and	(ds_lista_titulo_p IS NOT NULL AND ds_lista_titulo_p::text <> '');


BEGIN

if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') and (ds_lista_titulo_p IS NOT NULL AND ds_lista_titulo_p::text <> '') then

	select	a.dt_prev_pagto
	into STRICT	dt_prev_pagto_w
	from	negociacao_cr a
	where	a.nr_sequencia	= nr_seq_negociacao_p;

	open c01;
	loop
	fetch c01 into
		nr_titulo_w,
		vl_saldo_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	count(*)
		into STRICT	qt_registro_w
		from	titulo_rec_negociado a
		where	a.nr_seq_negociacao	= nr_seq_negociacao_p
		and	a.nr_titulo		= nr_titulo_w;

		if (qt_registro_w = 0) then

			vl_juros_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_prev_pagto_w, clock_timestamp()),'R','J');
			vl_multa_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_prev_pagto_w, clock_timestamp()),'R','M');

			insert	into	titulo_rec_negociado(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nr_seq_negociacao,
				nr_titulo,
				vl_negociado,
				vl_juros,
				vl_multa)
			values (nextval('titulo_rec_negociado_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_negociacao_p,
				nr_titulo_w,
				vl_saldo_titulo_w,
				vl_juros_w,
				vl_multa_w);
		end if;
		end;
	end loop;
	close c01;

	CALL atualizar_valores_neg_cr(nr_seq_negociacao_p,nm_usuario_p,'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
