CREATE OR REPLACE PROCEDURE inserir_todos_ganho_perda ( nr_atendimento_p bigint, nr_cirurgia_p bigint, nr_seq_lista_tipo_p text, cd_setor_atendimento_p bigint, dt_ocorrencia_p timestamp, qt_volume_p bigint, ie_liberado_p text, nr_hora_p bigint, ds_observacao_p text, nm_usuario_p text, ds_lista_inf_adic_p text, qt_ocorrencia_p bigint, nr_seq_pepo_p bigint) AS $body$
DECLARE

 
nr_seq_lista_tipo_w	varchar(255);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;
nr_seq_w		bigint;

BEGIN
 
 
if (nr_seq_lista_tipo_p IS NOT NULL AND nr_seq_lista_tipo_p::text <> '')	then 
	begin	 
	nr_seq_lista_tipo_w := nr_seq_lista_tipo_p;
	while(nr_seq_lista_tipo_w IS NOT NULL AND nr_seq_lista_tipo_w::text <> '') loop 
		begin 
		nr_pos_virgula_w := position(',' in nr_seq_lista_tipo_w);
		if (nr_pos_virgula_w > 0)	then 
			begin 
			nr_sequencia_w		:= (substr(nr_seq_lista_tipo_w,1,nr_pos_virgula_w-1))::numeric;
			nr_seq_lista_tipo_w	:= substr(nr_seq_lista_tipo_w,nr_pos_virgula_w+1,length(nr_seq_lista_tipo_w));
			end;
		else 
			begin 
			nr_sequencia_w		:= (nr_seq_lista_tipo_w)::numeric;
			nr_seq_lista_tipo_w	:= null;			
			end;
		end if;
		if ( nr_sequencia_w > 0) then 
			begin 
			nr_seq_w := inserir_ganho_perda(nr_atendimento_p, nr_cirurgia_p, nr_sequencia_w, cd_setor_atendimento_p, dt_ocorrencia_p, qt_volume_p, ie_liberado_p, nr_hora_p, ds_observacao_p, nm_usuario_p, ds_lista_inf_adic_p, qt_ocorrencia_p, nr_seq_pepo_p, nr_seq_w);
			end;
		end if;			
		end;
	end loop;
	end;
end if;
 
commit;
 
end;
