CREATE OR REPLACE PROCEDURE inserir_unidades_regra ( nr_seq_regra_servico_p bigint, nm_usuario_p text, ds_lista_unidades_p text) AS $body$
DECLARE


nr_seq_regra_servico_unid_w			bigint;
nr_seq_unidade_w				bigint;
ds_lista_unidades_w			varchar(4000);
ie_pos_virgula_w				smallint;
tam_lista_w				bigint;
cd_setor_atendimento_w			integer;


BEGIN

if (ds_lista_unidades_p IS NOT NULL AND ds_lista_unidades_p::text <> '') then

    ds_lista_unidades_w := ds_lista_unidades_p;

    while (ds_lista_unidades_w IS NOT NULL AND ds_lista_unidades_w::text <> '') loop
		begin
		ie_pos_virgula_w	:= position(',' in ds_lista_unidades_w);
		tam_lista_w			:= length(ds_lista_unidades_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_unidade_w			:= substr(ds_lista_unidades_w,1,(ie_pos_virgula_w - 1));
		else
			nr_seq_unidade_w			:= ds_lista_unidades_w;
		end if;

		ds_lista_unidades_w	:= substr(ds_lista_unidades_w,(ie_pos_virgula_w + 1),tam_lista_w);

		select	nextval('sl_regra_servico_unid_seq')
		into STRICT	nr_seq_regra_servico_unid_w
		;

		select	cd_setor_atendimento
		into STRICT	cd_Setor_atendimento_w
		from	sl_regra_servico
		where	nr_sequencia = nr_seq_regra_servico_p;

		insert into sl_regra_servico_unid(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_unidade,
						nr_seq_regra_servico,
						cd_setor_atendimento
		)
		values (
				nr_seq_regra_servico_unid_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_unidade_w,
				nr_seq_regra_servico_p,
				cd_setor_atendimento_w
		);
		end;
	end loop;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
