CREATE OR REPLACE PROCEDURE inserir_versao_doc_projeto ( nr_seq_documento_p bigint, ds_arquivo_p text, ds_arquivo_diretorio_p text, nm_usuario_p text) AS $body$
DECLARE


nr_versao_w		bigint;


BEGIN
	select	max(a.nr_versao) + 1
	into STRICT	nr_versao_w
	from	proj_documento_versao a
	where	a.nr_seq_documento = nr_seq_documento_p;

	if (coalesce(nr_versao_w::text, '') = '') then
		nr_versao_w := 1;
	end if;


	insert	into proj_documento_versao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_documento,
		ds_arquivo,
		ds_arquivo_diretorio,
		nr_versao)
	values (nextval('proj_documento_versao_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_documento_p,
		ds_arquivo_p,
		ds_arquivo_diretorio_p,
		nr_versao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
