CREATE OR REPLACE PROCEDURE inserir_worklist_integracao ( cd_especialidade_p text, cd_pessoa_fisica_med_dest_p text, cd_pessoa_fisica_p text, nm_usuario_p text default null) AS $body$
DECLARE


nr_seq_grupo_reg_w	bigint;										
nr_seq_work_item_w	bigint;
nm_usuario_w	varchar(15);
cd_pessoa_fisica_w varchar(10);

dt_fim_w		timestamp;	


BEGIN

Select  max('T.I.E') -- Integração Bifrost
into STRICT	nm_usuario_w
;

select	max(nr_seq_grupo_regulacao)
into STRICT 	nr_seq_grupo_reg_w
from 	regra_regulacao
where	ie_tipo = 'RC'
and		coalesce(ie_tipo_parecer,'R') = 'C'
and		coalesce(cd_especialidade,coalesce(cd_especialidade_p,0)) = coalesce(cd_especialidade_p,0)
and		coalesce(cd_medico_dest,coalesce(cd_pessoa_fisica_med_dest_p,0)) = coalesce(cd_pessoa_fisica_med_dest_p,0);	

if (nr_seq_grupo_reg_w IS NOT NULL AND nr_seq_grupo_reg_w::text <> '')then

	select  max( nr_seq_work_item )
	into STRICT	nr_seq_work_item_w
	from	grupo_regulacao_lib
	where 	nr_seq_grupo_regulacao = nr_seq_grupo_reg_w
	and 	coalesce(ie_situacao,'A') = 'A';
	
	if (nr_seq_work_item_w IS NOT NULL AND nr_seq_work_item_w::text <> '')then
	
		select (clock_timestamp() + interval '30 days')
		into STRICT	dt_fim_w
		;
		
		
		CALL gerar_registro_worklist(null, clock_timestamp(), nm_usuario_w,clock_timestamp(),dt_fim_w,cd_pessoa_fisica_p,nr_seq_work_item_w);
		
	end if;

end if;	
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
