CREATE OR REPLACE PROCEDURE insert_call_center (ie_tipo_pessoa_p text, cd_localizado_p text, nm_contato_p text, nr_contato_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		pls_segurado.nr_sequencia%type	:= null;
nr_seq_pagador_w		pls_contrato_pagador.nr_sequencia%type	:= null;
nr_seq_contrato_w		pls_contrato.nr_sequencia%type	:= null;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type	:= null;
nr_seq_congenere_w		pls_congenere.nr_sequencia%type	:= null;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type	:= null;
cd_cgc_w				pessoa_juridica.cd_cgc%type	:= null;
ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_pessoa_p = 'P') then
	cd_pessoa_fisica_w	:= cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'PJ') then
	cd_cgc_w			:= cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'B') or (ie_tipo_pessoa_p = 'BI') then
	nr_seq_segurado_w	:=	cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'PG') then
	nr_seq_pagador_w	:= 	cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'E') then
	nr_seq_contrato_w	:=  cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'PR') then
	nr_seq_prestador_w	:= cd_localizado_p;
elsif (ie_tipo_pessoa_p = 'OP') then
	nr_seq_congenere_w	:= cd_localizado_p;
end if;

if (coalesce(cd_pessoa_fisica_w::text, '') = '') or (coalesce(cd_cgc_w::text, '') = '') then
	ds_retorno_w	:= pls_obter_cod_pessoa_atend(cd_localizado_p,ie_tipo_pessoa_p);

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		if (substr(ds_retorno_w,position(',' in ds_retorno_w) + 1,2) = 'PF') then
			cd_pessoa_fisica_w	:= substr(ds_retorno_w,1,position(',' in ds_retorno_w) - 1);
		elsif (substr(ds_retorno_w,position(',' in ds_retorno_w) + 1,2) = 'PJ') then
			cd_cgc_w	:= substr(ds_retorno_w,1,position(',' in ds_retorno_w) - 1);
		end if;
	end if;
end if;

insert into pls_atendimento(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_estabelecimento,
	ie_tipo_pessoa,
	nr_seq_segurado,
	nr_seq_pagador,
	nr_seq_contrato,
	nr_seq_prestador,
	nr_seq_congenere,
	cd_pessoa_fisica,
	cd_cgc,
	ie_status,
	nm_contato,
	nr_telefone)
values (nextval('pls_atendimento_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_estabelecimento_p,
	ie_tipo_pessoa_p,
	nr_seq_segurado_w,
	nr_seq_pagador_w,
	nr_seq_contrato_w,
	nr_seq_prestador_w,
	nr_seq_congenere_w,
	cd_pessoa_fisica_w,
	cd_cgc_w,
	'A',
	nm_contato_p,
	nr_contato_p);

commit;

end;
$body$
