SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE insert_days_procedure ( nr_seq_proc_cpoe_p bigint, nr_seq_proc_interno_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w      cpoe_dias_procedimento.nr_sequencia%type;

cursor_days CURSOR FOR
SELECT	nr_sequencia,
        dt_procedimento
from	cpoe_dias_procedimento
where	nr_seq_proc_cpoe = nr_seq_proc_cpoe_p;

BEGIN

for r_cursor_days in cursor_days loop

	select	nextval('cpoe_procedimento_seq')
	 into STRICT	nr_sequencia_w
	from	cpoe_procedimento LIMIT 1;
	
	insert into cpoe_procedimento(
			nr_sequencia,
			qt_procedimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_atendimento,
			nr_seq_proc_interno,
			cd_perfil_ativo,
			dt_prev_execucao,
			ie_duracao)
		values (
			nr_sequencia_w,
			1,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_atendimento_p,
			nr_seq_proc_interno_p,
			obter_perfil_ativo,
			r_cursor_days.dt_procedimento,
			'P');
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
