SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE insert_eclipse_claim_history ( nr_interno_conta_p bigint, --account number patient billing
 nm_usuario_p text, ds_history_p text, nr_response_code_p bigint ) AS $body$
DECLARE


nr_seq_history_claim_w	eclipse_claim_history.nr_sequencia%type;	
cd_claim_w		dva_claim.cd_claim%type;
ds_error_desc_w		eclipse_status_codes.ds_code%type;


BEGIN

select	nextval('eclipse_claim_history_seq')
into STRICT	nr_seq_history_claim_w
;

select	max(cd_claim)	claim_val 	
into STRICT	cd_claim_w
from	dva_claim
where	nr_interno_conta = nr_interno_conta_p;

select	max(ds_code)
into STRICT	ds_error_desc_w
from 	eclipse_status_codes
where	cd_code =  nr_response_code_p;

insert	into	eclipse_claim_history(	
			nr_sequencia	,
			nm_usuario	,
			dt_atualizacao 	,
			ds_historico	, 
			nr_interno_conta
		)
	values (	
			nr_seq_history_claim_w,
			nm_usuario_p,
			clock_timestamp(),
			CASE WHEN coalesce(ds_error_desc_w::text, '') = '' THEN (wheb_mensagem_pck.get_texto(1105901) ||  ' ' ||  cd_claim_w ||  ' '  || ds_history_p)  ELSE (wheb_mensagem_pck.get_texto(1105901)  ||  ' ' ||  cd_claim_w ||  ' '  || ds_error_desc_w) END ,
			nr_interno_conta_p
		);
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
