CREATE OR REPLACE PROCEDURE insert_emergency_data_dpe ( cd_pessoa_fisica_p text, ds_dpe_xml_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_w		emergency_data_nfdm.nr_sequencia%type;
cd_pessoa_fisica_w	emergency_data_nfdm.cd_pessoa_fisica%type;


BEGIN

begin
    select	max(cd_pessoa_fisica)
    into STRICT	cd_pessoa_fisica_w
    from	emergency_data_nfdm where cd_pessoa_fisica = cd_pessoa_fisica_p;
    exception
        when no_data_found then
        cd_pessoa_fisica_w := null;
end;

if (coalesce(cd_pessoa_fisica_w::text, '') = '') then

    select	nextval('emergency_data_nfdm_seq')
    into STRICT    nr_sequencia_w
	;

	insert into emergency_data_nfdm(
        nr_sequencia,
		cd_pessoa_fisica,
		ds_dpe,
		nm_usuario,
		dt_atualizacao)
	values (
        nr_sequencia_w,
		cd_pessoa_fisica_p,
		ds_dpe_xml_p,
		nm_usuario_p,
		clock_timestamp());
else

    update	emergency_data_nfdm set
		ds_dpe		= ds_dpe_xml_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_pessoa_fisica		= cd_pessoa_fisica_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
