SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE insert_equip_waiting_list (nr_seq_lista_p bigint, lista_equip_p text) AS $body$
DECLARE


lista_equip_w			varchar(400);
ie_contador_w			bigint := 0;
tam_lista_w				bigint;
ie_pos_virgula_w		smallint;
cd_equipamento_w		bigint;
nr_seq_classif_equip_w	bigint;
ie_tipo_w				varchar(1);
cd_estabelecimento_w    integer;
cd_perfil_w            	integer;
nm_usuario_w            varchar(15);


BEGIN

nm_usuario_w 			:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w 	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w 			:= wheb_usuario_pck.get_cd_perfil;


select	coalesce(max(obter_valor_param_usuario(871, 100, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w)),'C')
into STRICT	ie_tipo_w
;

cd_equipamento_w		:= null;
nr_seq_classif_equip_w	:= null;

lista_equip_w := lista_equip_p;

while (lista_equip_w IS NOT NULL AND lista_equip_w::text <> '') or ie_contador_w > 200 loop
	tam_lista_w			:= length(lista_equip_w);
	ie_pos_virgula_w	:= position(',' in lista_equip_w);

	if (ie_pos_virgula_w <> 0) then
		if (ie_tipo_w = 'E') then
			cd_equipamento_w := substr(lista_equip_w, 1, (ie_pos_virgula_w - 1));
		else
			nr_seq_classif_equip_w := substr(lista_equip_w, 1, (ie_pos_virgula_w - 1));
		end if;
		
		lista_equip_w := substr(lista_equip_w, (ie_pos_virgula_w + 1), tam_lista_w);
	end if;

	insert into agenda_lista_espera_equip(
		nr_sequencia,
		nr_seq_lista,
		dt_atualizacao,
		nm_usuario,
		cd_equipamento,
		nr_seq_classif_equip,
		ie_origem_inf,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_obrigatorio
	) values (
		nextval('agenda_lista_espera_equip_seq'),
		nr_seq_lista_p,
		clock_timestamp(),
		nm_usuario_w,
		cd_equipamento_w,
		nr_seq_classif_equip_w,
		'I',
		clock_timestamp(),
		nm_usuario_w,
		'S'
	);

	ie_contador_w := ie_contador_w + 1;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
