CREATE OR REPLACE PROCEDURE insert_era_response_details ( nr_seq_response_p bigint, nm_usuario_p text, nr_account_p text, vl_benefit_amount_p bigint, cd_claim_channel_p text, cd_claim_p text, dt_lodgement_p timestamp, cd_transaction_p text, nr_previous_account_p text, nr_previous_id_p text) AS $body$
DECLARE

										
nr_sequecia_w			era_response_details.nr_sequencia%type;
vl_benefit_amount_w   	era_response_details.vl_benefit_amount%type;
count_w 				integer;


BEGIN

										
select	nextval('era_response_details_seq')
into STRICT	nr_sequecia_w
;

select 	coalesce(count(nr_sequencia),0)
into STRICT 	count_w
from 	era_response_details
where 	cd_transaction = cd_transaction_p;

if (count_w = 0) then
	vl_benefit_amount_w := vl_benefit_amount_p / 100;
	
	insert into era_response_details(
					nr_sequencia,
					nr_seq_response,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_account,
					vl_benefit_amount,
					cd_claim_channel,
					cd_claim,
					dt_lodgement,
					cd_transaction,
					nr_previous_account,
					nr_previous_id)
				values (
					nr_sequecia_w,
					nr_seq_response_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_account_p,
					vl_benefit_amount_w,
					cd_claim_channel_p,
					cd_claim_p,
					dt_lodgement_p,
					cd_transaction_p,
					nr_previous_account_p,
					nr_previous_id_p);
end if;

commit;

end;
$body$
