CREATE OR REPLACE PROCEDURE insert_into_via_aplic_mims ( cd_imp_material_p imp_mat_via_aplic.cd_material%type, IE_VIA_APLICACAO_P imp_mat_via_aplic.IE_VIA_APLICACAO%type) AS $body$
DECLARE


-- nr_seq_mat_via_aplic_w mat_via_aplic.nr_sequencia%type; 
cd_material_w mat_via_aplic.cd_material%type;

c_imp_app_mims_unit_w CURSOR FOR  
SELECT *
from	imp_mat_via_aplic a 
where  	a.cd_material = cd_imp_material_p and a.IE_VIA_APLICACAO = IE_VIA_APLICACAO_P
and 	a.ie_dirty_check = 1;

BEGIN 

select	cd_material_tasy 
into STRICT	cd_material_w
from 	mims_material_tasy 
where	cd_imp_material = cd_imp_material_p;

for r_imp_app_mims_unit_w in c_imp_app_mims_unit_w loop

	/* select max(nr_sequencia+1)
	into	nr_seq_mat_via_aplic_w
	from	mat_via_aplic; */
        insert into mat_via_aplic(nr_sequencia,
			cd_material, 
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario, 
			nm_usuario_nrec, 
			ie_via_aplicacao, 
			ds_recomendacao, 
			ie_diluicao, 
			cd_setor_atendimento, 
			cd_setor_excluir, 
			ie_recomendada, 
			nr_seq_prioridade, 
			cd_estabelecimento) 
	SELECT	nextval('mat_via_aplic_seq'),
			cd_material_w, 
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario, 
			nm_usuario_nrec, 
			ie_via_aplicacao, 
			ds_recomendacao, 
			ie_diluicao, 
			cd_setor_atendimento, 
			cd_setor_excluir, 
			ie_recomendada, 
			nr_seq_prioridade, 
			cd_estabelecimento 
	from	imp_mat_via_aplic a 
	where	a.cd_material = cd_imp_material_p  and a.IE_VIA_APLICACAO = IE_VIA_APLICACAO_P;

end loop;

update	imp_mat_via_aplic a
set	a.ie_dirty_check = 0 
where	a.cd_material = cd_imp_material_p and IE_VIA_APLICACAO = IE_VIA_APLICACAO_P;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
