CREATE OR REPLACE PROCEDURE insert_laudo_pac_img ( nr_laudo_p bigint, ds_arquivo_p text) AS $body$
DECLARE


max_seq_w	bigint;


BEGIN

select coalesce(max(nr_seq_imagem), 0)
into STRICT max_seq_w
from laudo_paciente_imagem
where nr_sequencia = nr_laudo_p;

INSERT INTO laudo_paciente_imagem(
		nr_sequencia,
		nr_seq_imagem,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		ds_arquivo_imagem)
VALUES (
		nr_laudo_p,
		max_seq_w + 1,
		clock_timestamp(),
		clock_timestamp(),
		'ARQ_IMPORTADO',
		'ARQ_IMPORTADO',
		ds_arquivo_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
