SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE insert_mims_medic_ficha () AS $body$
DECLARE

  nr_count_w bigint;
  c_medic_ficha_tecnica CURSOR FOR
    SELECT *
    FROM medic_ficha_tecnica
    WHERE (nr_seq_superior IS NOT NULL AND nr_seq_superior::text <> '')
    AND (nr_seq_dcb IS NOT NULL AND nr_seq_dcb::text <> '');
BEGIN
  FOR r_medic_ficha_tecnica IN c_medic_ficha_tecnica
  LOOP
    SELECT COUNT(1)
    INTO STRICT nr_count_w
    FROM medic_ficha_tecnica
    WHERE nr_seq_dcb     = r_medic_ficha_tecnica.nr_seq_dcb
    AND coalesce(nr_seq_superior::text, '') = '';
    IF ( nr_count_w       = 0 ) THEN
      INSERT
      INTO medic_ficha_tecnica(
          nr_sequencia,
          dt_atualizacao,
          nm_usuario,
          ds_substancia,
          ds_sinonimo,
          ds_observacao,
          nr_seq_dci,
          nr_seq_dcb,
          dt_atualizacao_nrec,
          nm_usuario_nrec,
          ie_antimicrobiano,
          nr_seq_superior,
          ie_margem_terap_estreita,
          nr_externo
        )
        VALUES (
          nextval('medic_ficha_tecnica_seq'),
          clock_timestamp(),
          r_medic_ficha_tecnica.nm_usuario,
          r_medic_ficha_tecnica.ds_substancia,
          r_medic_ficha_tecnica.ds_sinonimo,
          r_medic_ficha_tecnica.ds_observacao,
          r_medic_ficha_tecnica.nr_seq_dci,
          r_medic_ficha_tecnica.nr_seq_dcb,
          clock_timestamp(),
          r_medic_ficha_tecnica.nm_usuario,
          r_medic_ficha_tecnica.ie_antimicrobiano,
          NULL ,
          r_medic_ficha_tecnica.ie_margem_terap_estreita,
          r_medic_ficha_tecnica.nr_externo
        );
    END IF;
  END LOOP;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
