cd_procedencia_w	procedencia.cd_procedencia%type;

BEGIN

if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '')	then
    select	max(cd_procedencia)
    into STRICT	cd_procedencia_w
    from	procedencia
    where	lower(ds_procedencia) = lower(ds_observacao_p);
