CREATE OR REPLACE PROCEDURE insert_pre_agenda_mipres ( nr_seq_agenda_aux_p agenda_paciente_auxiliar.nr_sequencia%type, dt_validity_mipres_p prescr_mipres.dt_validity_mipres%type default null, nr_prescr_mipres_p prescr_mipres.nr_presc_mipres%type default null, nm_usuario_p prescr_mipres.nm_usuario%type DEFAULT NULL, cd_pessoa_fisica_p prescr_mipres.cd_pessoa_fisica%type DEFAULT NULL, ds_erro_p INOUT text DEFAULT NULL) AS $body$
DECLARE


nr_seq_prescr_mipres_w		prescr_mipres.nr_sequencia%type;
dt_validity_mipres_w      	prescr_mipres.dt_validity_mipres%type;



BEGIN

	select  max(nr_sequencia),
			max(dt_validity_mipres)
	into STRICT    nr_seq_prescr_mipres_w, 
			dt_validity_mipres_w
	from    prescr_mipres
	where   nr_presc_mipres = nr_prescr_mipres_p;

  if (coalesce(nr_seq_prescr_mipres_w::text, '') = '') then

	select nextval('prescr_mipres_seq') 
	into STRICT nr_seq_prescr_mipres_w 
	;
	
	insert into prescr_mipres(
                nr_sequencia,
                nm_usuario,
                dt_atualizacao,
                dt_validity_mipres,
                nr_presc_mipres,
                cd_pessoa_fisica,
				ie_status)
    values (    nr_seq_prescr_mipres_w,
                nm_usuario_p,
                clock_timestamp(),
                dt_validity_mipres_p,
                nr_prescr_mipres_p,
                cd_pessoa_fisica_p,
				1);

    update  agenda_paciente_auxiliar
    set     nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp(),
            nr_seq_prescr_mipres = nr_seq_prescr_mipres_w
    where   nr_sequencia = nr_seq_agenda_aux_p;

  else
	  if (dt_validity_mipres_w != dt_validity_mipres_p ) then
        ds_erro_p	:= wheb_mensagem_pck.get_texto(1212870);
        /*rollback because at this point a register was inserted at table agenda_paciente_auxiliar*/

        rollback;
    else
      update  agenda_paciente_auxiliar
      set     nm_usuario = nm_usuario_p,
              dt_atualizacao = clock_timestamp(),
              nr_seq_prescr_mipres = nr_seq_prescr_mipres_w
      where   nr_sequencia = nr_seq_agenda_aux_p;

    end if;

  end if;

commit;

end;
$body$
