CREATE OR REPLACE PROCEDURE insert_princ_ativo_plan ( ds_princ_ativo_p text, ds_concentracao_p text, nr_seq_medic_p bigint) AS $body$
DECLARE


ds_princ_ativo_w 	varchar(255);
ds_concentracao_w 	varchar(255);WITH RECURSIVE cte AS (


c01 CURSOR FOR
	SELECT 	ltrim(rtrim(regexp_substr(ds_princ_ativo_p ,'[^+]+', 1, level))),
			ltrim(rtrim(regexp_substr(ds_concentracao_p,'[^;]+', 1, level)))
	
	ltrim(rtrim(regexp_substr(ds_princ_ativo_p , '[^+]+', 1, level))) is not null  UNION ALL


c01 CURSOR FOR
	SELECT 	ltrim(rtrim(regexp_substr(ds_princ_ativo_p ,'[^+]+', 1, level))),
			ltrim(rtrim(regexp_substr(ds_concentracao_p,'[^;]+', 1, level)))
	
	ltrim(rtrim(regexp_substr(ds_princ_ativo_p , '[^+]+', 1, level))) is not null JOIN cte c ON ()

) SELECT * FROM cte;
;


BEGIN

	open c01;
	loop
	fetch c01 into
		ds_princ_ativo_w,
		ds_concentracao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into plano_medic_info_adic(
				nr_sequencia,
				ds_principio_ativo,
				ds_concentracao,
				nr_seq_medic_uso,
				nm_usuario,
				nm_usuario_nrec,
				dt_atualizacao,
				dt_atualizacao_nrec)
	values ( 	nextval('plano_medic_info_adic_seq'),
				ds_princ_ativo_w,
				ds_concentracao_w,
				nr_seq_medic_p,
				wheb_usuario_pck.get_nm_usuario,
				wheb_usuario_pck.get_nm_usuario,
				clock_timestamp(),
				clock_timestamp());
	end;
	end loop;
	close c01;

end;
$body$
