CREATE OR REPLACE PROCEDURE insert_problem_rotina_pac ( nm_usuario_p lista_problema_pac.nm_usuario%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_cad_problema_p lista_problema_pac.nr_seq_cad_problema%type, ie_liberar_informacao_p text) AS $body$
DECLARE


nr_sequencia_w		lista_problema_pac.nr_sequencia%type;
cd_pessoa_fisica_w	lista_problema_pac.cd_pessoa_fisica%type;
cd_doenca_w		cadastro_problema.cd_doenca%type;
ds_problema_w		cadastro_problema.ds_problema%type;


BEGIN

select	max(nr_sequencia) + 1
into STRICT	nr_sequencia_w
from	lista_problema_pac;

select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

select	cd_doenca,
	ds_problema
into STRICT	cd_doenca_w,
	ds_problema_w
from	cadastro_problema
where	nr_sequencia = nr_seq_cad_problema_p;

insert into lista_problema_pac(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_intensidade,
			cd_pessoa_fisica,
			dt_inicio,
			nr_atendimento,
			cd_doenca,
			ds_problema,
			ie_situacao,
			ie_nivel_atencao,
			cd_medico,
			nr_seq_cad_problema,
			ie_importado_historico,
			dt_liberacao
			)
		values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'L',
			cd_pessoa_fisica_w,
			clock_timestamp(),
			nr_atendimento_p,
			cd_doenca_w,
			ds_problema_w,
			'A',
			'T',
			substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10),
			nr_seq_cad_problema_p,
			'N',
			CASE WHEN coalesce(ie_liberar_informacao_p,'N')='N' THEN  null  ELSE clock_timestamp() END
			);

commit;

if (coalesce(ie_liberar_informacao_p,'N') = 'S') then
	CALL alterar_finalizar_problema(nr_sequencia_w, 'L', null, null, nm_usuario_p, null, 'LB', null, null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
