CREATE OR REPLACE PROCEDURE insert_procedimento_pac_medico ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint, dt_procedimento_p timestamp, nr_atendimento_p bigint, qt_procedimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_pac_medico_w	bigint;
qt_records_w			bigint;

C01 CURSOR FOR
		SELECT pcs.cd_procedimento_sus cd_procedimento_sus, pcs.ie_origem_proced_sus ie_origem_proced_sus
		from procedimento_conv_sus pcs, procedimento p
		where  pcs.cd_procedimento_sus=p.cd_procedimento
		and pcs.ie_origem_proced_sus=p.ie_origem_proced
		and pcs.cd_procedimento = cd_procedimento_p
		and pcs.ie_origem_proced= ie_origem_proced_p
		and p.ie_situacao='A';

BEGIN
	for c01_w in C01 loop
	begin
		
		select 	count(1)
		into STRICT	qt_records_w
		from	procedimento_pac_medico
		where	nr_atendimento 		= nr_atendimento_p
		and    	cd_procedimento 	= c01_w.cd_procedimento_sus
		and 	ie_situacao 		= 'A'
		and	ie_origem_proced 	= c01_w.ie_origem_proced_sus;
		
		if (qt_records_w = 0) then
			select	nextval('procedimento_pac_medico_seq')
			into STRICT	nr_sequencia_pac_medico_w
			;
		
			insert into procedimento_pac_medico(
				NR_SEQUENCIA,
				CD_SETOR_ATENDIMENTO,
				CD_PROCEDIMENTO,
				DT_ATUALIZACAO,
				DT_ATUALIZACAO_NREC,
				DT_INATIVACAO,
				DT_LIBERACAO,
				DT_PROCEDIMENTO,
				IE_ORIGEM_PROCED,
				IE_PROC_PRINC,
				IE_SITUACAO,
				NM_USUARIO,
				NM_USUARIO_INATIVACAO,
				NM_USUARIO_NREC,
				NR_ATENDIMENTO,
				NR_SEQ_EPISODIO,
				NR_SEQ_PROC_INTERNO,
				QT_PROCEDIMENTO
			) values (
				nr_sequencia_pac_medico_w,
				cd_setor_atendimento_p,
				c01_w.cd_procedimento_sus,
				clock_timestamp(),
				clock_timestamp(),
				null,
				null,
				dt_procedimento_p,
				c01_w.ie_origem_proced_sus,
				'N',
				'A',
				nm_usuario_p,
				null,
				nm_usuario_p,
				nr_atendimento_p,
				CASE WHEN coalesce(obter_episodio_atendimento(nr_atendimento_p),0)=0 THEN null  ELSE obter_episodio_atendimento(nr_atendimento_p) END ,
				null,
				qt_procedimento_p
			);
		end if;
	end;
	end loop;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
