CREATE OR REPLACE PROCEDURE insert_service_order_sp_hotfix ( nr_service_order_p service_order_sp_hotfix.nr_service_order%type, nr_service_pack_hotfix_p service_order_sp_hotfix.nr_service_pack_hotfix%type, nm_usuario_p service_order_sp_hotfix.nm_usuario%type, ie_commit_p text default 'S') AS $body$
DECLARE


nr_sequencia_w service_order_sp_hotfix.nr_sequencia%type;
cd_cnpj_w service_order_sp_hotfix.cd_cnpj%type;


BEGIN

  select max(nr_sequencia)
  into STRICT nr_sequencia_w
  from service_order_sp_hotfix
  where nr_service_pack_hotfix = nr_service_pack_hotfix_p
  and nr_service_order = nr_service_order_p;

  select max(a.cd_cnpj)
  into STRICT cd_cnpj_w
  from com_cliente a,
       man_ordem_servico b
  where a.nr_sequencia = b.nr_seq_cliente
  and b.nr_sequencia = nr_service_order_p;

  if (coalesce(nr_sequencia_w::text, '') = '') then
    insert into service_order_sp_hotfix(
      nr_sequencia,
      dt_atualizacao,
      nm_usuario,
      nr_service_pack_hotfix,
      nr_service_order,
	  cd_cnpj)
    values (
      nextval('service_order_sp_hotfix_seq'),
      clock_timestamp(),
      nm_usuario_p,
      nr_service_pack_hotfix_p,
      nr_service_order_p,
	  cd_cnpj_w);
	if (coalesce(ie_commit_p, 'S') = 'S') then
      commit;
	end if;
  end if;
end;
$body$
