CREATE OR REPLACE PROCEDURE insert_treatment_plan_team ( nr_sequencia_p bigint, cd_funcao_medico_p bigint, cd_pessoa_fisica_p text, cd_person_type_p text, nm_usuario_p text) AS $body$
DECLARE


cd_person_w		bigint;
cd_physician_w	bigint;
cd_cargo_w		bigint;


BEGIN
	cd_physician_w := null;
	cd_person_w := null;
	
	if (cd_person_type_p = '1' or cd_person_type_p = '2') then
		cd_physician_w := cd_pessoa_fisica_p;
	
	else
		cd_person_w := cd_pessoa_fisica_p;
	end if;
	
	select max(cd_cargo)
	into STRICT cd_cargo_w
	from funcao_medico
	where cd_funcao = cd_funcao_medico_p;
	
	insert into med_treatment_plan_team(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_med_treat_plan,
		cd_physician,
		cd_person,
		cd_position,
		si_person_type)
	values (
		nextval('med_treatment_plan_team_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		cd_physician_w,
		cd_person_w,
		cd_cargo_w,
		cd_person_type_p);
	
	commit;

end;
$body$
