CREATE OR REPLACE PROCEDURE insert_update_sus_financ ( cd_financiamento_p text, ds_financiamento_p text, dt_competencia_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_competencia_w	timestamp;
nr_sequencia_w		sus_financiamento.nr_sequencia%type;


BEGIN

dt_competencia_w	:= to_date(DT_COMPETENCIA_p,'yyyymm');

begin
select	nr_sequencia
into STRICT	nr_sequencia_w
from 	sus_financiamento
where	cd_financiamento = cd_financiamento_p  LIMIT 1;
exception
when others then
	nr_sequencia_w := 0;
end;

if (coalesce(nr_sequencia_w,0) > 0) then
	begin

	update	sus_financiamento
	set	ds_financiamento = ds_financiamento_p,
		dt_atualizacao	= clock_timestamp(),
		dt_competencia	= dt_competencia_w,
		nm_usuario 	= nm_usuario_p
	where	nr_sequencia 	= nr_sequencia_w;

	end;
else
	begin

	select	nextval('sus_financiamento_seq')
	into STRICT	nr_sequencia_w
	;

	insert into sus_financiamento(
		nr_sequencia,
		cd_financiamento,
		ds_financiamento,
		dt_competencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (nr_sequencia_w,
		cd_financiamento_p,
		ds_financiamento_p,
		dt_competencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p);

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
