CREATE OR REPLACE PROCEDURE insert_vital_sign_nut_asmnt (nr_sequencia_p bigint, nr_atendimento_p bigint, username_p text , cd_profesional_p atendimento_sinal_vital.CD_PESSOA_FISICA%type) AS $body$
DECLARE

  qt_peso_w atendimento_sinal_vital.qt_peso%type;
  qt_altura_cm_w atendimento_sinal_vital.qt_altura_cm%type;
  nr_atendimento_w bigint;

BEGIN
  SELECT nr_atendimento,
    qt_peso_atual,
    qt_altura
  INTO STRICT nr_atendimento_w,
    qt_peso_w,
    qt_altura_cm_w
  FROM aval_nutricao
  WHERE nr_sequencia = nr_sequencia_p;
  IF ((qt_peso_w IS NOT NULL AND qt_peso_w::text <> '') OR (qt_altura_cm_w IS NOT NULL AND qt_altura_cm_w::text <> '')) THEN
    INSERT INTO atendimento_sinal_vital(
        nr_sequencia,
        nr_atendimento,
        dt_sinal_vital,
        nm_usuario,
        qt_peso,
        qt_altura_cm,
        dt_atualizacao,
        ie_situacao,
        dt_liberacao,
        cd_paciente,
        cd_pessoa_fisica
      )
      VALUES (
        nextval('atendimento_sinal_vital_seq'),
        nr_atendimento_w,
        clock_timestamp(),
        username_p,
        qt_peso_w,
        qt_altura_cm_w,
        clock_timestamp(),
        'A',
        clock_timestamp(),
        obter_pessoa_atendimento(nr_atendimento_w,'C'),
        cd_profesional_p);
    COMMIT;
  END IF;
END;
$body$
