CREATE OR REPLACE PROCEDURE insert_waiting_list_cpoe (NR_SEQ_PROC_INTERNO_P bigint, CD_PESSOA_FISICA_P bigint, CD_PROCEDIMENTO_P bigint DEFAULT NULL, IE_ORIGEM_PROCEDIMENTO_P text DEFAULT NULL, QT_SOLICITADO_P bigint DEFAULT NULL, CD_MATERIAL_P bigint DEFAULT NULL, CD_MATERIAL_ENVIO_P bigint DEFAULT NULL, NM_USUARIO_P text DEFAULT NULL, NR_SEQ_CPOE_PROCEDIMENTO_P bigint DEFAULT NULL, DT_DESEJADA_P timestamp DEFAULT NULL, NR_MINUTO_DURACAO_P bigint DEFAULT NULL, CD_POSICAO_P bigint DEFAULT NULL, IE_LADO_P text DEFAULT NULL) AS $body$
DECLARE

NM_USUARIO_W AGENDA_LISTA_ESPERA.NM_USUARIO%type;
CD_DOENCA_W PROC_INTERNO_DOENCA.CD_DOENCA_CID%type;
CD_DEPARTAMENTO_MEDICO_W CPOE_ORDER_UNIT.CD_DEPARTAMENTO_MED%type;


BEGIN
  IF (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') THEN
    NM_USUARIO_W := coalesce(WHEB_USUARIO_PCK.GET_NM_USUARIO,NM_USUARIO_P);

    SELECT MAX(CD_DOENCA_CID)
    INTO STRICT CD_DOENCA_W 
    FROM PROC_INTERNO_DOENCA 
    WHERE NR_SEQ_PROC_CPOE = NR_SEQ_CPOE_PROCEDIMENTO_P;

	SELECT MAX(a.CD_DEPARTAMENTO_MED)
	into STRICT CD_DEPARTAMENTO_MEDICO_W
	FROM CPOE_ORDER_UNIT a,
	CPOE_PROCEDIMENTO b
	where a.NR_SEQUENCIA= b.NR_SEQ_CPOE_ORDER_UNIT
	and b.NR_SEQUENCIA= NR_SEQ_CPOE_PROCEDIMENTO_P;

    INSERT INTO AGENDA_LISTA_ESPERA(
            NR_SEQUENCIA,
            CD_PROCEDIMENTO,
            IE_ORIGEM_PROCED,
            QT_PROCEDIMENTO,
            CD_PESSOA_FISICA,
            DT_AGENDAMENTO,
            DT_ATUALIZACAO,
            DT_ATUALIZACAO_NREC,
            DT_PERIODO_INICIAL,
            IE_STATUS_ESPERA,
            NM_USUARIO_AGENDA,
            NM_USUARIO,
            NM_USUARIO_NREC,
            CD_MATERIAL,
            CD_MATERIAL_ENVIO,
            NR_SEQ_PROC_INTERNO,
            NR_SEQ_CPOE_PROCEDIMENTO,
            NM_PESSOA_LISTA,
            DT_DESEJADA,
            NR_MINUTO_DURACAO,
            CD_TIPO_AGENDA,
            IE_POSICAO,
            CD_CID,
			IE_LADO,
			CD_DEPARTAMENTO_MEDICO)
            VALUES (
            nextval('agenda_lista_espera_seq'),
            CD_PROCEDIMENTO_P,
            IE_ORIGEM_PROCEDIMENTO_P,
            QT_SOLICITADO_P,
            CD_PESSOA_FISICA_P,
            clock_timestamp(),
            clock_timestamp(),
            clock_timestamp(),
            clock_timestamp(),
            'A',
            NM_USUARIO_W,
            NM_USUARIO_W,
            NM_USUARIO_W,
            CD_MATERIAL_P,
            CD_MATERIAL_ENVIO_P,
            NR_SEQ_PROC_INTERNO_P,
            NR_SEQ_CPOE_PROCEDIMENTO_P,
            OBTER_NOME_PESSOA_FISICA(CD_PESSOA_FISICA_P, null),
            DT_DESEJADA_P,
            NR_MINUTO_DURACAO_P,
            1,
            CD_POSICAO_P,
            CD_DOENCA_W,
			IE_LADO_P,
			CD_DEPARTAMENTO_MEDICO_W);
    COMMIT;
  END IF;
END;
$body$
