CREATE OR REPLACE PROCEDURE intcom_baixar_item_solic ( nr_ordem_compra_p bigint, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_solic_compra_w				bigint;
nr_cot_compra_w				bigint;
nr_item_cot_compra_w			integer;
dt_baixa_w				timestamp;


BEGIN

update	solic_compra_item
set 	dt_baixa 	= clock_timestamp(),
	ds_observacao	= substr(WHEB_MENSAGEM_PCK.get_texto(303112,'DS_OBSERVACAO_W='|| ds_observacao ||';NR_ORDEM_COMPRA_P='|| NR_ORDEM_COMPRA_P),1,255) /*#@DS_OBSERVACAO_W#@ Baixa efetuada na geração da Ordem= #@NR_ORDEM_COMPRA_P#@*/
where	nr_solic_compra 	= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p
and	coalesce(dt_baixa::text, '') = '';


CALL gerar_hist_solic_sem_commit(
			nr_solic_compra_p,
			Wheb_mensagem_pck.get_Texto(303113), /*'Baixa do item',*/
			WHEB_MENSAGEM_PCK.get_texto(303116,'NR_ITEM_SOLIC_COMPRA_P='|| NR_ITEM_SOLIC_COMPRA_P ||';NR_ORDEM_COMPRA_P='|| NR_ORDEM_COMPRA_P), /*Baixa do item #@NR_ITEM_SOLIC_COMPRA_P#@ efetuada na procedure INTCOM_BAIXAR_ITEM_SOLIC, na geração da Ordem= #@NR_ORDEM_COMPRA_P#@*/
			'B',
			nm_usuario_p);

update	solic_compra
set	dt_baixa		= clock_timestamp()
where	nr_solic_compra	= nr_solic_compra_p
and	not exists (
	SELECT 1
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_p
	and	coalesce(dt_baixa::text, '') = '');

select	dt_baixa
into STRICT	dt_baixa_w
from	solic_compra
where	nr_solic_compra	= nr_solic_compra_p;

if (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then
	CALL gerar_hist_solic_sem_commit(
			nr_solic_compra_p,
			Wheb_mensagem_pck.get_Texto(303117), /*'Baixa do solicitação',*/
			Wheb_mensagem_pck.get_Texto(303119, 'NR_ORDEM_COMPRA_P='|| NR_ORDEM_COMPRA_P), /*'A solicitação foi baixada pela procedure INTCOM_BAIXAR_ITEM_SOLIC, na geração da Ordem='
 || nr_ordem_compra_p,*/
			'B',
			nm_usuario_p);
end if;

update	processo_aprov_compra a
set	a.ie_aprov_reprov = 'B',
	a.ds_observacao = substr(WHEB_MENSAGEM_PCK.get_texto(303120,'DS_OBSERVACAO_W='|| a.ds_observacao ||';NR_ORDEM_COMPRA_P='|| nr_ordem_compra_p),1,2000)
where	a.nr_sequencia in (
	SELECT	distinct(nr_seq_aprovacao)
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_p)
and	ie_aprov_reprov = 'P'
and	not exists (
	SELECT	1
	from	solic_compra_item x
	where	x.nr_seq_aprovacao = a.nr_sequencia
	and	coalesce(dt_baixa::text, '') = '');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
