CREATE OR REPLACE PROCEDURE intcom_exclui_cotacao_sem_item ( nr_cot_compra_p bigint) AS $body$
DECLARE


nr_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	nr_registro_w
from	ordem_compra_item
where	nr_cot_compra = nr_cot_compra_p;

if (nr_registro_w = 0) then

	delete from cot_compra
	where nr_cot_compra = nr_cot_compra_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
