CREATE OR REPLACE PROCEDURE intcom_gerar_int_cancel_sc ( nr_solic_compra_p bigint, ds_xml_p text, nm_usuario_p text, nr_Sequencia_p INOUT bigint) AS $body$
DECLARE

 
nr_seq_registro_w	bigint;				
ds_login_w		varchar(255);
ds_senha_w		varchar(255);
nr_documento_externo_w	varchar(255);
nr_sequencia_w		bigint;


BEGIN 
 
select	nextval('registro_integr_compras_seq') 
into STRICT	nr_seq_registro_w
;
 
select	a.ds_login_integr_compras_ws, 
	a.ds_senha_integr_compras_ws, 
	b.nr_documento_externo 
into STRICT	ds_login_w, 
	ds_senha_w, 
	nr_documento_externo_w 
from	parametro_compras a, 
	solic_compra b 
where	a.cd_estabelecimento	= b.cd_estabelecimento 
and	b.nr_solic_compra	= nr_solic_compra_p;
 
insert into registro_integr_compras( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_tipo_operacao, 
	nr_solic_compra) 
values (	nr_seq_registro_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p,	 
	'WAU', 
	nr_solic_compra_p);
	 
select	nextval('registro_integr_com_xml_seq') 
into STRICT	nr_sequencia_w
;	
 
insert into registro_integr_com_xml( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	nr_seq_registro, 
	ie_status, 
	ie_operacao, 
	ie_sistema_origem, 
	ds_erro, 
	ds_retorno, 
	--ds_xml, 
	ie_tipo_operacao, 
	ds_login, 
	ds_senha, 
	id_pdc) 
values (	nr_sequencia_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_seq_registro_w, 
	'NP', 
	'E', 
	'TASY', 
	null, 
	null, 
	--ds_xml_p, 
	'WAU', 
	ds_login_w, 
	ds_senha_w, 
	nr_documento_externo_w);
 
insert into solic_compra_hist(	 
	nr_sequencia, 
	nr_solic_compra, 
	dt_atualizacao, 
	nm_usuario, 
	dt_historico, 
	ds_titulo, 
	ds_historico, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_tipo, 
	cd_evento, 
	dt_liberacao) 
values (	nextval('solic_compra_hist_seq'), 
	nr_solic_compra_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	wheb_mensagem_pck.get_texto(303511), 
	wheb_mensagem_pck.get_texto(303513,'NR_DOCUMENTO_EXTERNO_W='||nr_documento_externo_w), 
	clock_timestamp(), 
	nm_usuario_p, 
	'S', 
	'U', 
	clock_timestamp());
 
update	solic_compra 
set	nr_documento_externo  = NULL 
where	nr_solic_compra = nr_solic_compra_p;
 
commit;
 
nr_Sequencia_p	:= nr_sequencia_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
