CREATE OR REPLACE PROCEDURE intcom_gerar_int_material ( cd_material_p bigint, ds_xml_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_registro_w	bigint;
ds_login_w		varchar(255);
ds_senha_w		varchar(255);			
				 

BEGIN 
 
select	nextval('registro_integr_compras_seq') 
into STRICT	nr_seq_registro_w
;
 
select	a.ds_login_integr_compras_ws, 
	a.ds_senha_integr_compras_ws 
into STRICT	ds_login_w, 
	ds_senha_w 
from	parametro_compras a 
where	a.cd_estabelecimento	= cd_estabelecimento_p;
 
insert into registro_integr_compras( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_tipo_operacao, 
	cd_material) 
values (	nr_seq_registro_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p,	 
	'WIP',	 
	cd_material_p);
 
insert into registro_integr_com_xml( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	nr_seq_registro, 
	ie_status, 
	ie_operacao, 
	ie_sistema_origem, 
	ds_erro, 
	ds_retorno, 
	ds_xml, 
	ie_tipo_operacao, 
	ds_login, 
	ds_senha) 
values (	nextval('registro_integr_com_xml_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_seq_registro_w, 
	'NP', 
	'E', 
	'TASY', 
	null, 
	null, 
	ds_xml_p, 
	'WIP', 
	ds_login_w, 
	ds_senha_w);
 
insert into material_historico(	 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ds_historico, 
	cd_material, 
	ds_titulo, 
	ie_tipo) 
values (	nextval('material_historico_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	substr(wheb_mensagem_pck.get_texto(315168),1,255), 
	cd_material_p, 
	substr(wheb_mensagem_pck.get_texto(315171),1,255), 
	'S');
	 
	 
commit;
 
end;
