CREATE OR REPLACE PROCEDURE intcom_gerar_int_ocorrencia ( nr_ordem_compra_p bigint, ds_xml_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_registro_w	bigint;
nr_solic_compra_w	bigint;
ds_login_w		varchar(255);
ds_senha_w		varchar(255);
nr_documento_externo_w	varchar(255);


BEGIN 
 
select	nextval('registro_integr_compras_seq') 
into STRICT	nr_seq_registro_w
;
 
select	max(nr_solic_compra) 
into STRICT	nr_solic_compra_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select	a.ds_login_integr_compras_ws, 
	a.ds_senha_integr_compras_ws, 
	b.nr_documento_externo 
into STRICT	ds_login_w, 
	ds_senha_w, 
	nr_documento_externo_w 
from	parametro_compras a, 
	ordem_compra b 
where	a.cd_estabelecimento	= b.cd_estabelecimento 
and	b.nr_ordem_compra	= nr_ordem_compra_p;
 
insert into registro_integr_compras( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_tipo_operacao, 
	nr_ordem_compra, 
	nr_solic_compra) 
values (	nr_seq_registro_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	'WOP', 
	nr_ordem_compra_p, 
	nr_solic_compra_w);
 
insert into registro_integr_com_xml( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	nr_seq_registro, 
	ie_status, 
	ie_operacao, 
	ie_sistema_origem, 
	ds_erro, 
	ds_retorno, 
	ds_xml, 
	ie_tipo_operacao, 
	ds_login, 
	ds_senha, 
	id_pdc) 
values (	nextval('registro_integr_com_xml_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_seq_registro_w, 
	'NP', 
	'E', 
	'TASY', 
	null, 
	null, 
	ds_xml_p, 
	'WOP', 
	ds_login_w, 
	ds_senha_w, 
	nr_documento_externo_w);
 
insert into ordem_compra_hist( 
	nr_sequencia, 
	nr_ordem_compra, 
	dt_atualizacao, 
	nm_usuario, 
	dt_historico, 
	ds_titulo, 
	ds_historico, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ie_tipo, 
	dt_liberacao) 
values (	nextval('ordem_compra_hist_seq'), 
	nr_ordem_compra_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	wheb_mensagem_pck.get_texto(303524), 
	wheb_mensagem_pck.get_texto(303526), 
	clock_timestamp(), 
	nm_usuario_p, 
	'S', 
	clock_timestamp());
 
 
commit;
 
end;
$body$
