CREATE OR REPLACE PROCEDURE intcom_gerar_item_cotacao ( cd_material_p bigint, qt_material_p bigint, nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, id_contrato_p bigint, nm_usuario_p text, nr_cot_compra_p bigint, nr_item_cot_compra_p INOUT bigint) AS $body$
DECLARE

 
 
cd_material_w				integer;
qt_material_w				double precision;
qt_existe_w				bigint;
nr_cot_compra_w				bigint;
nr_item_cot_compra_w			integer;
cd_unidade_medida_compra_w		varchar(30);
ds_material_direto_w			varchar(255);
dt_solic_item_w				timestamp;
nr_contrato_w				bigint;
qt_erro_w				bigint := 0;
ds_erro_w				varchar(2000) := '';
nr_seq_registro_w			bigint;
ds_login_w				varchar(255);
ds_senha_w				varchar(255);
cd_estabelecimento_w			bigint;
nr_pdc_w				varchar(100);


BEGIN 
 
nr_cot_compra_w := coalesce(nr_cot_compra_p,0);
 
select	coalesce(max(nr_cot_compra),0) 
into STRICT	nr_cot_compra_w 
from	cot_compra 
where	nr_cot_compra = nr_cot_compra_w;
 
if (nr_cot_compra_w > 0) then 
	begin 
 
	cd_material_w	:= cd_material_p;
	qt_material_w	:= replace(qt_material_p,'.',',');
	 
	select	cd_estabelecimento, 
		nr_documento_externo 
	into STRICT	cd_estabelecimento_w, 
		nr_pdc_w 
	from	cot_compra 
	where	nr_cot_compra = nr_cot_compra_w;
	 
	select	coalesce(max(nr_item_cot_compra),0) 
	into STRICT	nr_item_cot_compra_w 
	from	cot_compra_item 
	where	nr_cot_compra = nr_cot_compra_w 
	and	nr_solic_compra = nr_solic_compra_p 
	and	nr_item_solic_compra = nr_item_solic_compra_p;
	 
	if (nr_item_cot_compra_w = 0) then 
	 
		select	coalesce(max(nr_item_cot_compra),0) +1 
		into STRICT	nr_item_cot_compra_w 
		from	cot_compra_item 
		where	nr_cot_compra = nr_cot_compra_w;
	end if;
	 
	 
	 
	select	cd_unidade_medida_compra, 
		ds_material_direto, 
		coalesce(dt_solic_item,trunc(clock_timestamp() + interval '7 days')) 
	into STRICT	cd_unidade_medida_compra_w, 
		ds_material_direto_w, 
		dt_solic_item_w 
	from	solic_compra_item 
	where	nr_solic_compra = nr_solic_compra_p 
	and	nr_item_solic_compra = nr_item_solic_compra_p;	
	 
	if (id_contrato_p > 0) then 
		select	max(nr_sequencia) 
		into STRICT	nr_contrato_w 
		from	contrato 
		where	nr_documento_externo = id_contrato_p;
	end if;
	 
	begin 
	insert into cot_compra_item( 
		nr_cot_compra, 
		nr_item_cot_compra, 
		cd_material, 
		qt_material, 
		cd_unidade_medida_compra, 
		dt_atualizacao, 
		dt_limite_entrega, 
		nm_usuario, 
		ie_situacao, 
		ds_material_direto_w, 
	--	nr_solic_compra, 
	--	nr_item_solic_compra, 
		nr_contrato) 
	values (	nr_cot_compra_w, 
		nr_item_cot_compra_w, 
		cd_material_w, 
		qt_material_w, 
		cd_unidade_medida_compra_w, 
		clock_timestamp(), 
		dt_solic_item_w, 
		nm_usuario_p, 
		'A', 
		ds_material_direto_w, 
		--nr_solic_compra_p, 
	--	nr_item_solic_compra_p, 
		nr_contrato_w);
	exception 
	when others then 
		qt_erro_w	:= 1;
		ds_erro_w	:= substr(wheb_mensagem_pck.get_texto(303108) || sqlerrm,1,2000);
	end;
	 
	/* if	(qt_erro_w = 0) then		 
		 
		begin 
		insert into cot_compra_item_entrega( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_cot_compra, 
			nr_item_cot_compra, 
			dt_entrega, 
			qt_entrega) 
		values(	cot_compra_item_entrega_seq.nextval, 
			sysdate, 
			nm_usuario_p, 
			sysdate, 
			nm_usuario_p, 
			nr_cot_compra_w, 
			nr_item_cot_compra_w, 
			dt_solic_item_w, 
			qt_material_w); 
		exception 
		when others then 
			qt_erro_w	:= 1; 
			ds_erro_w	:= substr('Ocorreu erro ao gravar dados na tabela COT_COMPRA_ITEM_ENTREGA. ' || sqlerrm,1,2000); 
		end; 
	end if; */
 
		 
	/* if	(qt_erro_w = 0) then 
		 
		begin 
		update	solic_compra_item 
		set	nr_cot_compra		= nr_cot_compra_w, 
			nr_item_cot_compra	= nr_item_cot_compra_w 
		where	nr_solic_compra		= nr_solic_compra_p 
		and	nr_item_solic_compra	= nr_item_solic_compra_p; 
		exception 
		when others then 
			qt_erro_w	:= 1; 
			ds_erro_w	:= substr('Ocorreu erro ao alterar informações na tabela SOLIC_COMPRA_ITEM. ' || sqlerrm,1,2000); 
		end; 
	end if;	 */
 
	end;	
end if;
 
if (qt_erro_w > 0) then 
	 
	rollback;
	select	nextval('registro_integr_compras_seq') 
	into STRICT	nr_seq_registro_w 
	;
 
	select	a.ds_login_integr_compras_ws, 
		a.ds_senha_integr_compras_ws 
	into STRICT	ds_login_w, 
		ds_senha_w 
	from	parametro_compras a 
	where	a.cd_estabelecimento	= coalesce(cd_estabelecimento_w,1);
 
	insert into registro_integr_compras( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ie_tipo_operacao, 
		nr_solic_compra) 
	values (	nr_seq_registro_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p,	 
		'WDG',	 
		nr_solic_compra_p);
 
	insert into registro_integr_com_xml( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_registro, 
		ie_status, 
		ie_operacao, 
		ie_sistema_origem, 
		ds_erro, 
		ds_retorno, 
		ie_tipo_operacao, 
		ds_login, 
		ds_senha, 
		id_pdc) 
	values (	nextval('registro_integr_com_xml_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_registro_w, 
		'E', 
		'R', 
		'TASY', 
		ds_erro_w, 
		null, 
		'WDG', 
		ds_login_w, 
		ds_senha_w, 
		nr_pdc_w);
	commit;
	 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(182482,'DS_ERRO_P='||DS_ERRO_W);
end if;
 
nr_item_cot_compra_p := nr_item_cot_compra_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
