CREATE OR REPLACE PROCEDURE intcom_gravar_retorno_mat_int ( nr_seq_registro_p bigint, ie_status_p text, ie_sistema_origem_p text, ds_erro_p text, ds_retorno_p text, ds_xml_p text, nm_usuario_p text) AS $body$
DECLARE

qt_existe_w	bigint;
cd_material_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	registro_integr_com_xml
where	nr_seq_registro	= nr_seq_registro_p
and	ie_operacao	= 'E'
and	ie_status		= 'NP';

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(173194);
end if;

select	cd_material
into STRICT	cd_material_w
from	registro_integr_compras
where	nr_sequencia = nr_seq_registro_p;



insert into registro_integr_com_xml(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_registro,
	ie_status,
	ie_operacao,
	ie_sistema_origem,
	ds_erro,
	ds_retorno,
	ds_xml,
	ie_tipo_operacao)
values (	nextval('registro_integr_com_xml_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_registro_p,
	ie_status_p,
	'R',
	ie_sistema_origem_p,
	ds_erro_p,
	ds_retorno_p,
	ds_xml_p,
	'WIP');

if (ie_status_p = 'P') then

	insert into material_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_historico,
		cd_material,
		ds_titulo,
		ie_tipo)
	values (	nextval('material_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		WHEB_MENSAGEM_PCK.get_texto(278797),
		cd_material_w,
		WHEB_MENSAGEM_PCK.get_texto(278798),
		'S');

elsif (ie_status_p = 'E') then

	insert into material_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_historico,
		cd_material,
		ds_titulo,
		ie_tipo)
	values (	nextval('material_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		WHEB_MENSAGEM_PCK.get_texto(278799) || chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(278800) || ds_erro_p,
		cd_material_w,
		WHEB_MENSAGEM_PCK.get_texto(278802),
		'S');
end if;

update	registro_integr_com_xml
set	ie_status	= ie_status_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_seq_registro = nr_seq_registro_p
and	ie_operacao	= 'E';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
