-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE intdisp_gerar_mat_hor ( nr_prescricao_p bigint, ie_opcao_p text) AS $body$
DECLARE


ds_linha_arquivo_w			varchar(2000);
nr_seq_lote_w				bigint;
nm_arquivo_w        		varchar(100);
ds_diretorio_w        		varchar(255);
ds_diretorio_adep_w    		varchar(255);
nm_usuario_w        		varchar(15);
cd_estabelecimento_w    	smallint;
nr_seq_regra_disp_w    		bigint;
ie_gera_arquivo_w    		varchar(1);
ie_imprime_lote_w    		varchar(1);
ie_gerou_arq_w        		varchar(1) := 'N';
dt_horario_w        		timestamp;
dt_inicio_prescr_w    		timestamp;
dt_validade_prescr_w    	timestamp;
nr_atendimento_w    		bigint;
qt_dispensar_hor_w    		double precision;
qt_dose_w        			double precision;
cd_unidade_medida_w    		varchar(30);
ds_justificativa_w    		varchar(2000);
ie_forma_integracao_w   	varchar(1);
nr_sequencia_lote_w    		bigint;
cd_material_w        		integer;
nr_seq_turno_w        		bigint;
ds_turno_w        			varchar(80);
ds_classif_urgente_w    	varchar(80);
dt_dispensacao_w    		timestamp;
hr_dispensacao_w    		timestamp;
cd_setor_atendimento_w  	bigint;
ds_setor_atendimento_w  	varchar(100);
nm_paciente_w        		varchar(255);
cd_pessoa_fisica_w    		varchar(10);
ds_observacao_w        		varchar(400);
dt_liberacao_w        		timestamp;
ds_leito_w        			varchar(100);
nr_prescricao_w        		bigint;
dt_inicio_w       			timestamp;
dt_validade_w        		timestamp;
cd_medico_w        			varchar(10);
ie_classif_urgente_w    	varchar(3);
ds_intrucao_w        		varchar(255);
cd_material_generico_w  	integer;
ds_via_aplicacao_w    		varchar(255);
dt_lib_horario_w    		timestamp;
cd_crm_w        			varchar(20);
ds_material_w        		varchar(255);
ie_dispensar_farm_w    		varchar(1);
cd_prescritor_w        		varchar(10);
nr_seq_lote_ind_w    		varchar(15);
ds_local_estoque_w			varchar(255);
cd_local_estoque_w			integer;
nr_seq_estrut_w				bigint;
nr_seq_estrut_int_w			bigint;
ie_mat_estrutura_w			varchar(1);
cd_local_est_regra_w		integer;
nr_seq_mat_hor_w			bigint;
cd_local_estoque_hor_w		integer;
cd_intervalo_w				intervalo_prescricao.cd_intervalo%type;
ds_intervalo_w				intervalo_prescricao.ds_intervalo%type;
nr_seq_empresa_w			empresa_integracao.nr_sequencia%type;
ie_existe_w					varchar(1);
ie_swisslog_w				varchar(1);
qt_existe_disp_w			bigint;
cd_local_estoque_disp_w		local_estoque.cd_local_estoque%type;
ie_local_disp_eletronico_w	varchar(1);
nr_seq_material_w			prescr_material.nr_sequencia%type;
nr_seq_superior_w			prescr_material.nr_sequencia%type;

cd_perfil_ativo_w		integer;
ie_param_119_w			varchar(1):= 'N';
ie_revalida_w			varchar(1):= 'N';

/*
ie_opcao_p:
1 - Liberacao da prescricao
*/
C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			b.nr_prescricao,
			b.dt_inicio_prescr,
			b.dt_validade_prescr,
			b.nr_atendimento,
			a.cd_material,
			b.cd_medico,
			a.ie_classif_urgente,
			e.cd_material_generico,
			b.cd_estabelecimento,
			a.qt_dispensar_hor,
			a.qt_dose,
			a.cd_unidade_medida_dose,
			obter_desc_via(c.ie_via_aplicacao),
			obter_crm_medico(b.cd_medico),
			a.dt_lib_horario,
			e.ds_material,
			a.nr_seq_lote,
			a.ie_dispensar_farm,
			b.cd_prescritor,
			substr(obter_nome_pf(b.cd_pessoa_fisica),1,60) nm_paciente,
			a.dt_horario,
			c.cd_local_estoque,
			a.cd_local_estoque,
			b.cd_setor_atendimento,
			a.nr_seq_lote || '-' ||ROW_NUMBER() OVER (PARTITION BY a.nr_seq_lote order by a.nr_seq_lote) nr_seq_lote_ind,
			c.cd_intervalo,
			substr(obter_desc_intervalo_prescr(c.cd_intervalo),1,40) ds_intervalo,
			a.nr_seq_material,
			a.nr_seq_superior
	from    prescr_mat_hor a,
			prescr_medica b,
			prescr_material c,
			estrutura_material_v s,
			material e,
			ap_lote l
	where   (a.nr_seq_lote IS NOT NULL AND a.nr_seq_lote::text <> '')
	and    	a.cd_material = s.cd_material
	and     a.nr_prescricao = b.nr_prescricao
	and     c.nr_prescricao = b.nr_prescricao
	and     a.nr_seq_material = c.nr_sequencia
	and     a.nr_prescricao = nr_prescricao_p
	and     a.cd_material = e.cd_material
	and    	a.nr_seq_lote = l.nr_sequencia
	and     l.ie_status_lote = 'G'
	and     ((coalesce(c.ie_suspenso::text, '') = '') or (c.ie_suspenso <> 'S'))
	and     coalesce(a.dt_suspensao::text, '') = ''
	and		not exists (	SELECT	1
						from	int_disp_mat_hor x
						where	x.nr_seq_mat_hor = a.nr_sequencia
						and		x.nr_prescricao = a.nr_prescricao)
	and 	exists(	select	1
					from	regra_local_dispensacao x
					where	x.nr_sequencia 	= coalesce(a.nr_regra_local_disp, c.nr_seq_regra_local)
					and 	((coalesce(x.nr_seq_classif,a.nr_seq_classif) = a.nr_seq_classif) or (exists (	select	1
																										from	prescr_mat_hor w
																										where 	w.nr_prescricao = a.nr_prescricao
																										and 	w.nr_seq_material = a.nr_seq_superior
																										and     w.nr_seq_classif = x.nr_seq_classif
																										and 	w.nr_regra_local_disp = a.nr_regra_local_disp)))
					and 	x.cd_local_estoque	= coalesce(a.cd_local_estoque, coalesce(b.cd_local_estoque,c.cd_local_estoque))
					and		((coalesce(x.nr_seq_tipo_estrutura::text, '') = '') or ((select max(p.nr_seq_tipo) from mat_estrutura p where p.nr_sequencia = x.nr_seq_estrut_int) = x.nr_seq_tipo_estrutura))
					and		((x.cd_material 	= s.cd_material) OR (x.cd_grupo_material	= s.cd_grupo_material) OR (x.cd_subgrupo_material = s.cd_subgrupo_material) OR (x.cd_classe_material	= s.cd_classe_material) or ((x.nr_seq_estrut_int IS NOT NULL AND x.nr_seq_estrut_int::text <> '') and consistir_se_mat_contr_estrut(x.nr_seq_estrut_int,s.cd_material) = 'S')))
					order by l.nr_sequencia;


BEGIN

select	cd_estabelecimento,
		nm_usuario,
		cd_perfil_ativo
into STRICT	cd_estabelecimento_w,
		nm_usuario_w,
		cd_perfil_ativo_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

ie_param_119_w := obter_param_usuario(7029, 119, cd_perfil_ativo_w, nm_usuario_w, cd_estabelecimento_w, ie_param_119_w);

if (ie_opcao_p = '1') then
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_mat_hor_w,
		nr_prescricao_w,
		dt_inicio_w,
		dt_validade_w,
		nr_atendimento_w,
		cd_material_w,
		cd_medico_w,
		ie_classif_urgente_w,
		cd_material_generico_w,
		cd_estabelecimento_w,
		qt_dispensar_hor_w,
		qt_dose_w,
		cd_unidade_medida_w,
		ds_via_aplicacao_w,
		cd_crm_w,
		dt_lib_horario_w,
		ds_material_w,
		nr_seq_lote_w,
		ie_dispensar_farm_w,
		cd_prescritor_w,
		nm_paciente_w,
		dt_horario_w,
		cd_local_estoque_w,
		cd_local_estoque_hor_w,
		cd_setor_atendimento_w,
		nr_seq_lote_ind_w,
		cd_intervalo_w,
		ds_intervalo_w,
		nr_seq_material_w,
		nr_seq_superior_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	select	nr_sequencia
	into STRICT	nr_seq_empresa_w
	from	empresa_integracao
	where	upper(nm_empresa) = 'SWISSLOG';

	select	'S'
	into STRICT	ie_existe_w
	from	far_setores_integracao
	where	nr_seq_empresa_int = nr_seq_empresa_w
	and	cd_setor_atendimento = cd_setor_atendimento_w;
	exception
	when others then
		nr_seq_empresa_w := 0;
		ie_existe_w := 'N';
	end;

	ie_swisslog_w := 'N';

	if (ie_existe_w = 'S') then
		select	coalesce(max('S'), 'N')
		into STRICT	ie_swisslog_w
		from	prescr_mat_hor a,
				prescr_medica b,
				(SELECT	coalesce(nr_seq_estrut_int,0) nr_seq_estrut_int,
						cd_estabelecimento,
						cd_local_estoque_req
				from	parametros_farmacia) c
		where	(a.nr_seq_lote IS NOT NULL AND a.nr_seq_lote::text <> '')
		and		a.qt_dispensar_hor > 0
		and		consistir_se_mat_contr_estrut(c.nr_seq_estrut_int,a.cd_material) = 'S'
		and 	exists ( select 1 from ap_lote x where x.nr_sequencia = a.nr_seq_lote and coalesce(x.dt_atend_farmacia::text, '') = '')
		and		a.cd_local_estoque = c.cd_local_estoque_req
		and		b.cd_estabelecimento = c.cd_estabelecimento
		and		a.nr_prescricao = b.nr_prescricao
		and		a.nr_sequencia = nr_seq_mat_hor_w;
	end if;

	ie_local_disp_eletronico_w := 'S';

	select	count(1)
	into STRICT	qt_existe_disp_w
	from	dis_regra_setor b,
		dis_regra_local_setor a
	where	a.nr_seq_dis_regra_setor = b.nr_sequencia
	and	b.cd_setor_atendimento = cd_setor_atendimento_w;

	if (qt_existe_disp_w > 0) then

		select	coalesce(max('S'), 'N')
		into STRICT	ie_local_disp_eletronico_w
		from	dis_regra_setor b,
			dis_regra_local_setor a
		where	a.nr_seq_dis_regra_setor = b.nr_sequencia
		and	cd_setor_atendimento = cd_setor_atendimento_w
		and	a.cd_local_estoque = coalesce(cd_local_estoque_hor_w, cd_local_estoque_w);

	end if;

	select	substr(obter_desc_local_estoque(coalesce(cd_local_estoque_hor_w, cd_local_estoque_w)),1,100) ds_local_estoque
	into STRICT	ds_local_estoque_w
	;

	if (ie_swisslog_w = 'N') and (ie_local_disp_eletronico_w = 'S') then
		begin

		if (ie_param_119_w = 'S') then
			select CASE WHEN coalesce(obter_material_reval(nr_seq_lote_w, cd_material_w), 'N')='N' THEN 'N'  ELSE 'S' END
			into STRICT ie_revalida_w
			;		
		end if;

		insert into int_disp_mat_hor(
			nr_sequencia,
			nr_prescricao,
			dt_inicio_prescr,
			dt_validade_prescr,
			nr_atendimento,
			cd_material,
			cd_medico,
			ds_classif_urgente,
			cd_material_generico,
			cd_estabelecimento,
			qt_dispensar_hor,
			qt_dose,
			cd_unidade_medida_dose,
			ds_via_adm,
			crm_medico,
			dt_lib_horario,
			ds_material,
			nr_seq_lote,
			ie_dispensar_farm,
			nm_prescritor,
			nm_paciente,
			dt_horario,
			nr_seq_lote_ind,
			ie_tipo_movimentacao,
			cd_local_estoque,
			ds_local_estoque,
			nr_seq_mat_hor,
			cd_intervalo,
			ds_intervalo,
			dt_atualizacao,
			nr_seq_material,
			nr_seq_superior,
			ie_revalida)
		values (	nextval('int_disp_mat_hor_seq'),
			nr_prescricao_w,
			dt_inicio_w,
			dt_validade_w,
			nr_atendimento_w,
			cd_material_w,
			cd_medico_w,
			ie_classif_urgente_w,
			cd_material_generico_w,
			cd_estabelecimento_w,
			qt_dispensar_hor_w,
			qt_dose_w,
			cd_unidade_medida_w,
			ds_via_aplicacao_w,
			cd_crm_w,
			dt_lib_horario_w,
			ds_material_w,
			nr_seq_lote_w,
			ie_dispensar_farm_w,
			cd_prescritor_w,
			nm_paciente_w,
			dt_horario_w,
			nr_seq_lote_ind_w,
			'EOA',
			coalesce(cd_local_estoque_hor_w,cd_local_estoque_w),
			ds_local_estoque_w,
			nr_seq_mat_hor_w,
			cd_intervalo_w,
			ds_intervalo_w,
			clock_timestamp(),
			nr_seq_material_w,
			nr_seq_superior_w,
			ie_revalida_w);

		end;
	end if;	
	end;
	end loop;
	close C01;
	end;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE intdisp_gerar_mat_hor ( nr_prescricao_p bigint, ie_opcao_p text) FROM PUBLIC;

