SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE integracao_intelbras (nm_usuario_p text) AS $body$
DECLARE


json_w        		philips_json;
json_data_w   		text;
dt_inicial_w 		usuario.cd_barras_numerico%type;
dt_final_w		usuario.cd_barras_numerico%type;


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select 	Round((cast(CURRENT_TIMESTAMP at time zone 'UTC'-5/24 as timestamp) - date '1970-01-01') * 86400),
		Round((cast(CURRENT_TIMESTAMP at time zone 'UTC'+1 as timestamp) - date '1970-01-01') * 86400)
	into STRICT 	dt_inicial_w,
		dt_final_w
	;

	json_w := philips_json();
	json_w.put('DT_INICIAL', dt_inicial_w);
  	json_w.put('DT_FINAL', dt_final_w);
	dbms_lob.createtemporary(json_data_w, true);
	json_w.(json_data_w);

	json_data_w := bifrost.send_integration_content('api.controle.acesso.intelbras.integration', json_data_w, nm_usuario_p);
		
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
