CREATE OR REPLACE PROCEDURE integrar_alvaro (NR_SEQ_LOTE_EXTERNO_P bigint) AS $body$
DECLARE


ds_params varchar(255);

C01 CURSOR FOR
	SELECT distinct pp.nr_prescricao, pm.cd_estabelecimento, pp.nr_seq_lote_externo
	from PRESCR_PROCEDIMENTO pp
	inner join prescr_medica pm on pm.nr_prescricao = pp.nr_prescricao
	where pp.nr_seq_lote_externo = NR_SEQ_LOTE_EXTERNO_P;

 c01_w c01%rowtype;


BEGIN
  open C01;
	loop
	fetch C01 into C01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		ds_params := 'nr_prescricao_p=' || C01_w.nr_prescricao || 
				';cd_estabelecimento_p=' || C01_w.cd_estabelecimento || 
				';nr_seq_lote_externo_p='|| C01_w.nr_seq_lote_externo ||
				';nr_sequencia_p=0;';
	        CALL gravar_agend_integracao(622, ds_params);
     	end;
	end loop;
	close C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
