SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE integra_cond_pagto_protheus ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cadastro_w			varchar(20);
ds_cadastro_w			varchar(80);
qt_registros_w			bigint;
cd_condicao_pagamento_w		bigint;
ie_operacao_w			varchar(15);
ie_erro_w			varchar(1) := 'N';


BEGIN

select	cd_cadastro,
	ds_cadastro,
	ie_operacao
into STRICT	cd_cadastro_w,
	ds_cadastro_w,
	ie_operacao_w
from	w_protheus_cadastro
where	nr_sequencia = nr_sequencia_p
and	tp_cadastro = 'CP';

if (coalesce(cd_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'CP', WHEB_MENSAGEM_PCK.get_texto(309639), nm_usuario_p); --O código da condição de pagamento recebida do Protheus está vazia.
	ie_erro_w := 'S';
end if;

if (coalesce(ds_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'CP', WHEB_MENSAGEM_PCK.get_texto(309641) , nm_usuario_p); --A descrição da condição de pagamento recebida do Protheus está vazia.
	ie_erro_w := 'S';
end if;

if (ie_erro_w = 'N') and (cd_cadastro_w IS NOT NULL AND cd_cadastro_w::text <> '') and (ds_cadastro_w IS NOT NULL AND ds_cadastro_w::text <> '') then
	begin

	if (ie_operacao_w in ('I','A')) then

		select	count(*)
		into STRICT	qt_registros_w
		from	condicao_pagamento
		where	cd_sistema_ant = cd_cadastro_w;

		if (qt_registros_w = 0) and (ie_operacao_w = 'I') then

			select	coalesce(max(cd_condicao_pagamento),0) + 1
			into STRICT	cd_condicao_pagamento_w
			from	condicao_pagamento;

			insert into condicao_pagamento(
				cd_condicao_pagamento,
				ds_condicao_pagamento,
				ie_forma_pagamento,
				ie_situacao,
				dt_atualizacao,
				nm_usuario,
				ie_contas_pagar,
				ie_acao_nao_util,
				ie_prazo_medio_eis,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_sistema_ant,
				ie_tipo_acrescimo,
				ie_consiste_vencto_nota,
				ie_plano_saude,
				ie_ops_quota_part,
				ie_aprova_direto,
				ie_fat_med_pep)
			values (
				cd_condicao_pagamento_w,
				substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,80),
				1,
				'A',
				clock_timestamp(),
				nm_usuario_p,
				'N',
				'M',
				'S',
				clock_timestamp(),
				nm_usuario_p,
				cd_cadastro_w,
				'P',
				'N',
				'N',
				'N',
				'N',
				'S');
		end if;

		if (qt_registros_w > 0) and (ie_operacao_w = 'A') then

			update	condicao_pagamento
			set	ds_condicao_pagamento	= substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,80),
				ie_situacao		= 'A',
				dt_atualizacao		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	cd_sistema_ant		= cd_cadastro_w;
		end if;

	elsif (ie_operacao_w = 'E') then

		update	condicao_pagamento
		set	ie_situacao = 'I',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_sistema_ant = cd_cadastro_w;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
