SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE integra_grupo_fleury ( nr_prescricao_p bigint) AS $body$
DECLARE


ie_grupa_horario_w	lab_parametro.ie_agrupa_ficha_fleury%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

ie_existe_w		varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	coalesce(max(a.cd_estabelecimento),wheb_usuario_pck.get_cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica a
	where	a.nr_prescricao = nr_prescricao_p;

	ie_grupa_horario_w	:= obter_dados_lab_parametro('AF', cd_estabelecimento_w);

	if (ie_grupa_horario_w <> 'H') then
		update	prescr_procedimento
		set	dt_integracao = clock_timestamp()
		where	nr_prescricao = nr_prescricao_p
		and	coalesce(dt_integracao::text, '') = ''
		and	nr_grupo_integracao = ( SELECT 	min(nr_grupo_integracao)
						from	prescr_procedimento
						where	nr_prescricao = nr_prescricao_p
						and	(nr_grupo_integracao IS NOT NULL AND nr_grupo_integracao::text <> '')
						and	coalesce(dt_integracao::text, '') = '');
	else
		update	prescr_proc_hor
		set	dt_integracao = clock_timestamp()
		where	nr_prescricao = nr_prescricao_p
		and	coalesce(dt_integracao::text, '') = ''
		and	nr_grupo_integracao = ( SELECT 	min(nr_grupo_integracao)
						from	prescr_proc_hor
						where	nr_prescricao = nr_prescricao_p
						and	(nr_grupo_integracao IS NOT NULL AND nr_grupo_integracao::text <> '')
						and	coalesce(dt_integracao::text, '') = '');

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_existe_w
		from	prescr_proc_hor a
		where	coalesce(dt_integracao::text, '') = ''
		and	a.nr_prescricao = nr_prescricao_p;

		if (ie_existe_w = 'N') then
			update	prescr_procedimento
			set	dt_integracao = clock_timestamp()
			where	nr_prescricao = nr_prescricao_p
			and	coalesce(dt_integracao::text, '') = '';
		end if;

	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
