SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE integra_natureza_protheus ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cadastro_w			varchar(20);
ds_cadastro_w			varchar(80);
qt_registros_w			bigint;
cd_natureza_operacao_w		smallint;
ie_operacao_w			varchar(15);
ie_erro_w			varchar(1) := 'N';


BEGIN

select	cd_cadastro,
	ds_cadastro,
	ie_operacao
into STRICT	cd_cadastro_w,
	ds_cadastro_w,
	ie_operacao_w
from	w_protheus_cadastro
where	nr_sequencia = nr_sequencia_p
and	tp_cadastro = 'NA';

if (coalesce(cd_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'NA', WHEB_MENSAGEM_PCK.get_texto(309918) , nm_usuario_p); --O código da natureza recebida do Protheus está vazia.
	ie_erro_w := 'S';
end if;

if (coalesce(ds_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'NA', WHEB_MENSAGEM_PCK.get_texto(309919) , nm_usuario_p); --A descrição da natureza recebida do Protheus está vazia.'
	ie_erro_w := 'S';
end if;

if (ie_erro_w = 'N') and (cd_cadastro_w IS NOT NULL AND cd_cadastro_w::text <> '') and (ds_cadastro_w IS NOT NULL AND ds_cadastro_w::text <> '') then
	begin

	if (ie_operacao_w in ('I','A')) then

		select	count(*)
		into STRICT	qt_registros_w
		from	natureza_operacao
		where	cd_sistema_ext = cd_cadastro_w;

		if (qt_registros_w = 0) and (ie_operacao_w = 'I') then


			select	coalesce(max(cd_natureza_operacao),0) + 1
			into STRICT	cd_natureza_operacao_w
			from	natureza_operacao;

			insert into natureza_operacao(
				cd_natureza_operacao,
				ds_natureza_operacao,
				ie_entrada_saida,
				ie_situacao,
				ie_transferencia_estab,
				ie_integra_contas_pagar,
				cd_sistema_ext,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_integra_contas_receber)
			values (
				cd_natureza_operacao_w,
				substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,255),
				'E',
				'A',
				'S',
				'S',
				cd_cadastro_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'N');
		end if;

		if (qt_registros_w > 0) and (ie_operacao_w = 'A') then

			update	natureza_operacao
			set	ds_natureza_operacao	= substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,255),
				ie_situacao		= 'A',
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
			where	cd_sistema_ext		= cd_cadastro_w;
		end if;

	elsif (ie_operacao_w = 'E') then

		update	natureza_operacao
		set	ie_situacao = 'I',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_sistema_ext = cd_cadastro_w;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
