SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE integra_unid_medida_protheus ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cadastro_w		varchar(20);
ds_cadastro_w		varchar(80);
ie_operacao_w			varchar(15);
qt_registros_w			bigint;
ie_erro_w			varchar(1) := 'N';


BEGIN

select	cd_cadastro,
	ds_cadastro,
	ie_operacao
into STRICT	cd_cadastro_w,
	ds_cadastro_w,
	ie_operacao_w
from	w_protheus_cadastro
where	nr_sequencia = nr_sequencia_p
and	tp_cadastro = 'UM';

if (coalesce(cd_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'UM', WHEB_MENSAGEM_PCK.get_texto(310127) , nm_usuario_p); --O código da unidade de medida recebida do Protheus está vazia.
	ie_erro_w := 'S';
end if;

if (coalesce(ds_cadastro_w::text, '') = '') then
	CALL gravar_log_protheus('ERRO', 'UM', WHEB_MENSAGEM_PCK.get_texto(310128) , nm_usuario_p); --A descrição da unidade de medida recebida do Protheus está vazia.
	ie_erro_w := 'S';
end if;

if (ie_erro_w = 'N') and (cd_cadastro_w IS NOT NULL AND cd_cadastro_w::text <> '') and (ds_cadastro_w IS NOT NULL AND ds_cadastro_w::text <> '') then
	begin

	if (ie_operacao_w in ('I','A')) then

		select	count(*)
		into STRICT	qt_registros_w
		from	unidade_medida
		where	cd_sistema_ant = cd_cadastro_w;

		if (qt_registros_w = 0) and (ie_operacao_w = 'I') then

			insert into unidade_medida(
				cd_unidade_medida,
				ds_unidade_medida,
				ie_situacao,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_fracao_dose,
				ie_permite_fracionar,
				ie_adm_diluicao,
				ie_mult_h_aplic,
				cd_sistema_ant)
			values (
				cd_cadastro_w,
				substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,40),
				'A',
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'N',
				'S',
				'N',
				'N',
				cd_cadastro_w);
		end if;

		if (qt_registros_w > 0) and (ie_operacao_w = 'A') then

			update	unidade_medida
			set	ds_unidade_medida		= substr(cd_cadastro_w || ' - ' || ds_cadastro_w,1,40),
				ie_situacao		= 'A',
				dt_atualizacao		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	cd_sistema_ant = cd_cadastro_w;
		end if;

	elsif (ie_operacao_w = 'E') then

		update	unidade_medida
		set	ie_situacao = 'I',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_sistema_ant = cd_cadastro_w;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
