SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE interditar_leito_ocupacao ( nm_usuario_p text, nr_seq_unidade_p bigint) AS $body$
DECLARE


nr_seq_servico_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_servico_w
from 	sl_unid_atend
where 	trunc(dt_prevista) = trunc(clock_timestamp())
and	nr_seq_unidade 	   = nr_seq_unidade_p
and	ie_status_serv 	   = 'P';

if (nr_seq_servico_w > 0) then
	update 	sl_unid_atend
	set	dt_atualizacao		=	clock_timestamp(),
		nm_usuario		= 	nm_usuario_p,
		dt_interdicao		= 	clock_timestamp(),
		nm_usuario_interd 	= 	nm_usuario_p,
		ie_status_serv		=	'IT',
		ie_status_serv_interd	=	ie_status_serv
	where 	nr_sequencia 		= 	nr_seq_servico_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
