CREATE OR REPLACE PROCEDURE interromper_tratamento (nr_seq_atendimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
update 	paciente_atendimento
set 	dt_interrompido = clock_timestamp(),
	nm_usuario_interrompido  = nm_usuario_p,
	dt_inicio_adm  = NULL
where 	nr_seq_atendimento = nr_seq_atendimento_p;

update	agenda_quimio
set	ie_status_agenda  = 'C',
	DT_CANCELADA 	  = clock_timestamp(),
	NM_USUARIO_CANCEL = nm_usuario_p
where	nr_seq_atendimento = nr_seq_atendimento_p;

commit;

end;
$body$
