SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE intpd_atualiza_reg_dependente (nr_sequencia_p bigint, ie_status_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT 	nr_sequencia,
	ie_status
from	intpd_fila_transmissao
where	nr_seq_superior = nr_sequencia_p;
c01_w	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	/*Atualiza o status dos registros dependentes para "Pendente" quando o registro superior Ã© processado*/

	if (coalesce(c01_w.nr_sequencia,0) > 0) and (coalesce(c01_w.ie_status,'X') = 'AS') and (ie_status_p = 'P') then
		update	intpd_fila_transmissao
		set	ie_status		= ie_status_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= c01_w.nr_sequencia;
	end if;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
